import { useState, useMemo } from 'react';

const SunPositionViz = () => {
  const [longitude, setLongitude] = useState(0);
  const [latitude, setLatitude] = useState(45);

  const width = 600;
  const height = 300;
  const padding = { top: 40, right: 40, bottom: 60, left: 60 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  // Calculate sun altitude for each hour
  const curveData = useMemo(() => {
    const points = [];
    const solarDeclination = 0; // Equinox for now (will add as variable later)
    const decRad = (solarDeclination * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;

    // Longitude offset: 15¬∞ = 1 hour
    const hourOffset = longitude / 15;

    for (let i = 0; i <= 240; i++) {
      const utcHour = i / 10; // 0 to 24 in 0.1 increments
      const localSolarHour = utcHour + hourOffset;

      // Hour angle: 0 at solar noon, +/- 180¬∞ at midnight
      const hourAngle = ((localSolarHour - 12) * 15 * Math.PI) / 180;

      // Sun altitude formula (simplified for equinox when declination = 0)
      // sin(altitude) = sin(lat)*sin(dec) + cos(lat)*cos(dec)*cos(hourAngle)
      const sinAltitude = 
        Math.sin(latRad) * Math.sin(decRad) + 
        Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);

      const altitude = (Math.asin(sinAltitude) * 180) / Math.PI;

      const x = padding.left + (utcHour / 24) * graphWidth;
      // Map altitude (-90 to 90) to graph coordinates
      const y = padding.top + ((90 - altitude) / 180) * graphHeight;

      points.push({ x, y, hour: utcHour, altitude });
    }
    return points;
  }, [longitude, latitude, graphWidth, graphHeight]);

  // Generate SVG path
  const pathD = curveData
    .map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`)
    .join(' ');

  // Horizon line (altitude = 0)
  const horizonY = padding.top + (90 / 180) * graphHeight;

  // Find sunrise/sunset times
  const sunrisePoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0
  );
  const sunsetPoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0
  );

  // Current sun position indicator (noon UTC as reference)
  const noonIndex = 120; // 12 hours * 10
  const noonPoint = curveData[noonIndex];

  return (
    <div style={{ 
      backgroundColor: '#1a1a1c', 
      padding: '20px', 
      borderRadius: '8px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#e9e9ea'
    }}>
      <h2 style={{ fontSize: '20px', margin: '0 0 16px 0' }}>
        Sun Altitude vs Time (UTC)
      </h2>

      <svg width={width} height={height} style={{ display: 'block' }}>
        {/* Background */}
        <rect 
          x={padding.left} 
          y={padding.top} 
          width={graphWidth} 
          height={graphHeight} 
          fill="#27272a" 
          rx="4"
        />

        {/* Day/night regions */}
        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
        </defs>

        {/* Fill below horizon (night) */}
        <rect 
          x={padding.left} 
          y={horizonY} 
          width={graphWidth} 
          height={padding.top + graphHeight - horizonY}
          fill="rgba(35, 35, 52, 0.5)"
          clipPath="url(#graphClip)"
        />

        {/* Horizon line */}
        <line 
          x1={padding.left} 
          y1={horizonY} 
          x2={padding.left + graphWidth} 
          y2={horizonY}
          stroke="#e67e22"
          strokeWidth="2"
          strokeDasharray="8,4"
        />
        <text 
          x={padding.left + graphWidth + 5} 
          y={horizonY + 4} 
          fill="#e67e22" 
          fontSize="12"
        >
          horizon
        </text>

        {/* Grid lines - horizontal (altitude) */}
        {[-60, -30, 30, 60, 90].map(alt => {
          const y = padding.top + ((90 - alt) / 180) * graphHeight;
          return (
            <g key={alt}>
              <line 
                x1={padding.left} 
                y1={y} 
                x2={padding.left + graphWidth} 
                y2={y}
                stroke="#393941"
                strokeWidth="1"
              />
              <text 
                x={padding.left - 8} 
                y={y + 4} 
                fill="#a1a1a8" 
                fontSize="12" 
                textAnchor="end"
              >
                {alt}¬∞
              </text>
            </g>
          );
        })}

        {/* Grid lines - vertical (time) */}
        {[0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          return (
            <g key={hour}>
              <line 
                x1={x} 
                y1={padding.top} 
                x2={x} 
                y2={padding.top + graphHeight}
                stroke="#393941"
                strokeWidth="1"
              />
              <text 
                x={x} 
                y={padding.top + graphHeight + 20} 
                fill="#a1a1a8" 
                fontSize="12" 
                textAnchor="middle"
              >
                {hour.toString().padStart(2, '0')}:00
              </text>
            </g>
          );
        })}

        {/* Sun path curve */}
        <path 
          d={pathD} 
          fill="none" 
          stroke="#f4d03f" 
          strokeWidth="3"
          clipPath="url(#graphClip)"
        />

        {/* Sunrise/sunset markers */}
        {sunrisePoint && (
          <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="6" fill="#e67e22" />
        )}
        {sunsetPoint && (
          <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="6" fill="#e25f73" />
        )}

        {/* Noon marker */}
        <circle cx={noonPoint.x} cy={noonPoint.y} r="5" fill="#8c7ae6" />

        {/* Axis labels */}
        <text 
          x={padding.left + graphWidth / 2} 
          y={height - 10} 
          fill="#e9e9ea" 
          fontSize="14" 
          textAnchor="middle"
        >
          Time (UTC)
        </text>
        <text 
          x={15} 
          y={padding.top + graphHeight / 2} 
          fill="#e9e9ea" 
          fontSize="14" 
          textAnchor="middle"
          transform={`rotate(-90, 15, ${padding.top + graphHeight / 2})`}
        >
          Sun Altitude (¬∞)
        </text>
      </svg>

      {/* Controls */}
      <div style={{ 
        marginTop: '20px', 
        display: 'flex', 
        gap: '40px',
        flexWrap: 'wrap'
      }}>
        <div style={{ flex: 1, minWidth: '200px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Longitude: <span style={{ color: '#8c7ae6' }}>{longitude}¬∞</span>
            <span style={{ color: '#a1a1a8', marginLeft: '8px', fontSize: '12px', fontStyle: 'italic' }}>
              (horizontal shift)
            </span>
          </label>
          <input 
            type="range" 
            min="-180" 
            max="180" 
            value={longitude}
            onChange={(e) => setLongitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', color: '#a1a1a8' }}>
            <span>-180¬∞ (West)</span>
            <span>0¬∞ (Prime)</span>
            <span>+180¬∞ (East)</span>
          </div>
        </div>

        <div style={{ flex: 1, minWidth: '200px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}¬∞</span>
            <span style={{ color: '#a1a1a8', marginLeft: '8px', fontSize: '12px', fontStyle: 'italic' }}>
              (amplitude)
            </span>
          </label>
          <input 
            type="range" 
            min="-90" 
            max="90" 
            value={latitude}
            onChange={(e) => setLatitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', color: '#a1a1a8' }}>
            <span>-90¬∞ (S Pole)</span>
            <span>0¬∞ (Equator)</span>
            <span>+90¬∞ (N Pole)</span>
          </div>
        </div>
      </div>

      {/* Info panel */}
      <div style={{ 
        marginTop: '20px', 
        padding: '16px', 
        backgroundColor: '#232334', 
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
          <div>
            <span style={{ color: '#a1a1a8' }}>Max altitude: </span>
            <span style={{ color: '#f4d03f' }}>
              {(90 - Math.abs(latitude)).toFixed(1)}¬∞
            </span>
          </div>
          <div>
            <span style={{ color: '#a1a1a8' }}>Solar noon (UTC): </span>
            <span style={{ color: '#8c7ae6' }}>
              {(12 - longitude / 15).toFixed(1).padStart(2, '0')}:00
            </span>
          </div>
          {sunrisePoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunrise: </span>
              <span style={{ color: '#e67e22' }}>
                {sunrisePoint.hour.toFixed(1)}h
              </span>
            </div>
          )}
          {sunsetPoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunset: </span>
              <span style={{ color: '#e25f73' }}>
                {sunsetPoint.hour.toFixed(1)}h
              </span>
            </div>
          )}
        </div>

        {Math.abs(latitude) > 66.5 && (
          <div style={{ 
            marginTop: '12px', 
            padding: '8px', 
            backgroundColor: '#27272a', 
            borderRadius: '4px',
            borderLeft: '3px solid #e67e22'
          }}>
            <span style={{ fontSize: '12px', fontStyle: 'italic', color: '#a1a1a8' }}>
              ‚ö† Arctic/Antarctic latitude ‚Äî at equinox, sun barely rises/sets. 
              Adding seasonal declination will show midnight sun / polar night.
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default SunPositionViz;

