import { useState, useMemo } from 'react';

const SunPositionViz = () => {
  const [longitude, setLongitude] = useState(0);
  const [latitude, setLatitude] = useState(45);
  const [dayOfYear, setDayOfYear] = useState(80); // ~March 21 equinox

  const width = 700;
  const height = 320;
  const padding = { top: 40, right: 60, bottom: 60, left: 60 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  // Calculate solar declination from day of year
  // Œ¥ = 23.45¬∞ √ó sin(360/365 √ó (d - 81))
  const declination = useMemo(() => {
    return 23.45 * Math.sin((2 * Math.PI / 365) * (dayOfYear - 81));
  }, [dayOfYear]);

  // Get approximate date from day of year
  const dateLabel = useMemo(() => {
    const date = new Date(2024, 0, 1);
    date.setDate(dayOfYear);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  }, [dayOfYear]);

  // Calculate sun altitude for each hour
  const curveData = useMemo(() => {
    const points = [];
    const decRad = (declination * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;
    const hourOffset = longitude / 15;

    for (let i = 0; i <= 240; i++) {
      const utcHour = i / 10;
      const localSolarHour = utcHour + hourOffset;
      const hourAngle = ((localSolarHour - 12) * 15 * Math.PI) / 180;

      // Full sun altitude formula
      const sinAltitude = 
        Math.sin(latRad) * Math.sin(decRad) + 
        Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);

      const altitude = (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;

      const x = padding.left + (utcHour / 24) * graphWidth;
      const y = padding.top + ((90 - altitude) / 180) * graphHeight;

      points.push({ x, y, hour: utcHour, altitude });
    }
    return points;
  }, [longitude, latitude, declination, graphWidth, graphHeight]);

  const pathD = curveData
    .map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`)
    .join(' ');

  const horizonY = padding.top + (90 / 180) * graphHeight;

  // Find max altitude
  const maxAltitude = Math.max(...curveData.map(p => p.altitude));
  const minAltitude = Math.min(...curveData.map(p => p.altitude));

  // Determine day type
  const dayType = useMemo(() => {
    if (minAltitude > 0) return 'midnight-sun';
    if (maxAltitude < 0) return 'polar-night';
    return 'normal';
  }, [minAltitude, maxAltitude]);

  // Find sunrise/sunset
  const sunrisePoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0
  );
  const sunsetPoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0
  );

  // Calculate daylight hours
  const daylightHours = useMemo(() => {
    if (dayType === 'midnight-sun') return 24;
    if (dayType === 'polar-night') return 0;
    const aboveHorizon = curveData.filter(p => p.altitude >= 0).length;
    return (aboveHorizon / curveData.length) * 24;
  }, [curveData, dayType]);

  // Key dates for reference
  const keyDates = [
    { day: 1, label: 'Jan 1' },
    { day: 80, label: 'Equinox' },
    { day: 172, label: 'Solstice' },
    { day: 266, label: 'Equinox' },
    { day: 355, label: 'Solstice' },
  ];

  return (
    <div style={{ 
      backgroundColor: '#1a1a1c', 
      padding: '20px', 
      borderRadius: '8px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#e9e9ea'
    }}>
      <h2 style={{ fontSize: '20px', margin: '0 0 16px 0' }}>
        Sun Altitude vs Time (UTC)
      </h2>

      <svg width={width} height={height} style={{ display: 'block' }}>
        <rect 
          x={padding.left} 
          y={padding.top} 
          width={graphWidth} 
          height={graphHeight} 
          fill="#27272a" 
          rx="4"
        />

        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
          <linearGradient id="sunGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#f4d03f" />
            <stop offset="100%" stopColor="#e67e22" />
          </linearGradient>
        </defs>

        {/* Night region below horizon */}
        <rect 
          x={padding.left} 
          y={horizonY} 
          width={graphWidth} 
          height={padding.top + graphHeight - horizonY}
          fill="rgba(35, 35, 52, 0.6)"
          clipPath="url(#graphClip)"
        />

        {/* Horizon line */}
        <line 
          x1={padding.left} 
          y1={horizonY} 
          x2={padding.left + graphWidth} 
          y2={horizonY}
          stroke="#e67e22"
          strokeWidth="2"
          strokeDasharray="8,4"
        />
        <text x={padding.left + graphWidth + 5} y={horizonY + 4} fill="#e67e22" fontSize="12">
          horizon
        </text>

        {/* Grid - altitude */}
        {[-60, -30, 30, 60, 90].map(alt => {
          const y = padding.top + ((90 - alt) / 180) * graphHeight;
          return (
            <g key={alt}>
              <line 
                x1={padding.left} y1={y} 
                x2={padding.left + graphWidth} y2={y}
                stroke="#393941" strokeWidth="1"
              />
              <text x={padding.left - 8} y={y + 4} fill="#a1a1a8" fontSize="12" textAnchor="end">
                {alt}¬∞
              </text>
            </g>
          );
        })}

        {/* Grid - time */}
        {[0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          return (
            <g key={hour}>
              <line 
                x1={x} y1={padding.top} 
                x2={x} y2={padding.top + graphHeight}
                stroke="#393941" strokeWidth="1"
              />
              <text 
                x={x} y={padding.top + graphHeight + 20} 
                fill="#a1a1a8" fontSize="12" textAnchor="middle"
              >
                {hour.toString().padStart(2, '0')}:00
              </text>
            </g>
          );
        })}

        {/* Sun path */}
        <path 
          d={pathD} 
          fill="none" 
          stroke="url(#sunGradient)" 
          strokeWidth="3"
          clipPath="url(#graphClip)"
        />

        {/* Sunrise/sunset markers */}
        {sunrisePoint && (
          <g>
            <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="7" fill="#e67e22" />
            <text x={sunrisePoint.x} y={sunrisePoint.y - 12} fill="#e67e22" fontSize="11" textAnchor="middle">
              ‚Üë
            </text>
          </g>
        )}
        {sunsetPoint && (
          <g>
            <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="7" fill="#e25f73" />
            <text x={sunsetPoint.x} y={sunsetPoint.y - 12} fill="#e25f73" fontSize="11" textAnchor="middle">
              ‚Üì
            </text>
          </g>
        )}

        {/* Max altitude marker */}
        {dayType !== 'polar-night' && (
          <g>
            {(() => {
              const maxPoint = curveData.reduce((a, b) => a.altitude > b.altitude ? a : b);
              return (
                <>
                  <circle cx={maxPoint.x} cy={maxPoint.y} r="5" fill="#8c7ae6" />
                  <line 
                    x1={maxPoint.x} y1={maxPoint.y + 8} 
                    x2={maxPoint.x} y2={horizonY - 2}
                    stroke="#8c7ae6" strokeWidth="1" strokeDasharray="4,4" opacity="0.5"
                  />
                </>
              );
            })()}
          </g>
        )}

        {/* Axis labels */}
        <text 
          x={padding.left + graphWidth / 2} y={height - 10} 
          fill="#e9e9ea" fontSize="14" textAnchor="middle"
        >
          Time (UTC)
        </text>
        <text 
          x={15} y={padding.top + graphHeight / 2} 
          fill="#e9e9ea" fontSize="14" textAnchor="middle"
          transform={`rotate(-90, 15, ${padding.top + graphHeight / 2})`}
        >
          Altitude (¬∞)
        </text>
      </svg>

      {/* Controls */}
      <div style={{ marginTop: '20px', display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
        {/* Longitude */}
        <div style={{ flex: 1, minWidth: '180px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Longitude: <span style={{ color: '#8c7ae6' }}>{longitude}¬∞</span>
          </label>
          <input 
            type="range" min="-180" max="180" value={longitude}
            onChange={(e) => setLongitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', color: '#a1a1a8' }}>
            <span>-180¬∞W</span><span>0¬∞</span><span>+180¬∞E</span>
          </div>
        </div>

        {/* Latitude */}
        <div style={{ flex: 1, minWidth: '180px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}¬∞</span>
          </label>
          <input 
            type="range" min="-90" max="90" value={latitude}
            onChange={(e) => setLatitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', color: '#a1a1a8' }}>
            <span>-90¬∞ S</span><span>0¬∞</span><span>+90¬∞ N</span>
          </div>
        </div>

        {/* Day of Year */}
        <div style={{ flex: 1.5, minWidth: '240px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Date: <span style={{ color: '#f4d03f' }}>{dateLabel}</span>
            <span style={{ color: '#a1a1a8', marginLeft: '8px', fontSize: '12px' }}>
              (Œ¥ = {declination.toFixed(1)}¬∞)
            </span>
          </label>
          <input 
            type="range" min="1" max="365" value={dayOfYear}
            onChange={(e) => setDayOfYear(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#f4d03f' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', color: '#a1a1a8' }}>
            {keyDates.map(d => (
              <span 
                key={d.day} 
                style={{ cursor: 'pointer', color: Math.abs(dayOfYear - d.day) < 10 ? '#f4d03f' : '#a1a1a8' }}
                onClick={() => setDayOfYear(d.day)}
              >
                {d.label}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Info panel */}
      <div style={{ 
        marginTop: '20px', 
        padding: '16px', 
        backgroundColor: '#232334', 
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap', alignItems: 'center' }}>
          <div>
            <span style={{ color: '#a1a1a8' }}>Max altitude: </span>
            <span style={{ color: '#f4d03f' }}>{maxAltitude.toFixed(1)}¬∞</span>
          </div>
          <div>
            <span style={{ color: '#a1a1a8' }}>Daylight: </span>
            <span style={{ color: '#8c7ae6' }}>{daylightHours.toFixed(1)}h</span>
          </div>
          {dayType === 'normal' && sunrisePoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunrise: </span>
              <span style={{ color: '#e67e22' }}>
                {Math.floor(sunrisePoint.hour)}:{((sunrisePoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}
          {dayType === 'normal' && sunsetPoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunset: </span>
              <span style={{ color: '#e25f73' }}>
                {Math.floor(sunsetPoint.hour)}:{((sunsetPoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}

          {dayType !== 'normal' && (
            <div style={{ 
              padding: '4px 12px', 
              borderRadius: '4px',
              backgroundColor: dayType === 'midnight-sun' ? 'rgba(244, 208, 63, 0.2)' : 'rgba(35, 35, 52, 0.8)',
              border: `1px solid ${dayType === 'midnight-sun' ? '#f4d03f' : '#8c7ae6'}`
            }}>
              {dayType === 'midnight-sun' ? '‚òÄÔ∏è Midnight Sun' : 'üåë Polar Night'}
            </div>
          )}
        </div>

        {/* Declination explanation */}
        <div style={{ 
          marginTop: '12px', 
          padding: '12px', 
          backgroundColor: '#27272a', 
          borderRadius: '4px',
          fontSize: '12px',
          color: '#a1a1a8'
        }}>
          <strong style={{ color: '#e9e9ea' }}>Solar Declination (Œ¥):</strong> The sun's latitude above/below the equator. 
          Ranges from <span style={{ color: '#6ab0f3' }}>-23.45¬∞</span> (Dec solstice) to 
          <span style={{ color: '#f4d03f' }}> +23.45¬∞</span> (June solstice).
          <br/>
