import { useState, useMemo } from 'react';

const SunPositionViz = () => {
  const [longitude, setLongitude] = useState(0);
  const [latitude, setLatitude] = useState(45);
  const [dayOfYear, setDayOfYear] = useState(172); // Summer solstice to show tilt effect
  const [axialTilt, setAxialTilt] = useState(23.45); // Earth's current obliquity

  const width = 700;
  const height = 320;
  const padding = { top: 40, right: 60, bottom: 60, left: 60 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  // Calculate solar declination from day of year AND axial tilt
  // Œ¥ = tilt √ó sin(360/365 √ó (d - 81))
  const declination = useMemo(() => {
    return axialTilt * Math.sin((2 * Math.PI / 365) * (dayOfYear - 81));
  }, [dayOfYear, axialTilt]);

  // Get approximate date from day of year
  const dateLabel = useMemo(() => {
    const date = new Date(2024, 0, 1);
    date.setDate(dayOfYear);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  }, [dayOfYear]);

  // Calculate sun altitude for each hour
  const curveData = useMemo(() => {
    const points = [];
    const decRad = (declination * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;
    const hourOffset = longitude / 15;

    for (let i = 0; i <= 240; i++) {
      const utcHour = i / 10;
      const localSolarHour = utcHour + hourOffset;
      const hourAngle = ((localSolarHour - 12) * 15 * Math.PI) / 180;

      const sinAltitude = 
        Math.sin(latRad) * Math.sin(decRad) + 
        Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);

      const altitude = (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;

      const x = padding.left + (utcHour / 24) * graphWidth;
      const y = padding.top + ((90 - altitude) / 180) * graphHeight;

      points.push({ x, y, hour: utcHour, altitude });
    }
    return points;
  }, [longitude, latitude, declination, graphWidth, graphHeight]);

  // Also compute equinox curve for comparison
  const equinoxCurve = useMemo(() => {
    const points = [];
    const decRad = 0; // Equinox
    const latRad = (latitude * Math.PI) / 180;
    const hourOffset = longitude / 15;

    for (let i = 0; i <= 240; i++) {
      const utcHour = i / 10;
      const localSolarHour = utcHour + hourOffset;
      const hourAngle = ((localSolarHour - 12) * 15 * Math.PI) / 180;

      const sinAltitude = Math.cos(latRad) * Math.cos(hourAngle);
      const altitude = (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;

      const x = padding.left + (utcHour / 24) * graphWidth;
      const y = padding.top + ((90 - altitude) / 180) * graphHeight;

      points.push({ x, y });
    }
    return points;
  }, [longitude, latitude, graphWidth, graphHeight]);

  const pathD = curveData
    .map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`)
    .join(' ');

  const equinoxPathD = equinoxCurve
    .map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`)
    .join(' ');

  const horizonY = padding.top + (90 / 180) * graphHeight;

  const maxAltitude = Math.max(...curveData.map(p => p.altitude));
  const minAltitude = Math.min(...curveData.map(p => p.altitude));

  const dayType = useMemo(() => {
    if (minAltitude > 0) return 'midnight-sun';
    if (maxAltitude < 0) return 'polar-night';
    return 'normal';
  }, [minAltitude, maxAltitude]);

  const sunrisePoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0
  );
  const sunsetPoint = curveData.find((p, i) => 
    i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0
  );

  const daylightHours = useMemo(() => {
    if (dayType === 'midnight-sun') return 24;
    if (dayType === 'polar-night') return 0;
    const aboveHorizon = curveData.filter(p => p.altitude >= 0).length;
    return (aboveHorizon / curveData.length) * 24;
  }, [curveData, dayType]);

  // Arctic/Antarctic circle latitude (depends on tilt!)
  const polarCircle = 90 - axialTilt;

  const keyDates = [
    { day: 1, label: 'Jan 1' },
    { day: 80, label: 'Equinox' },
    { day: 172, label: 'Solstice' },
    { day: 266, label: 'Equinox' },
    { day: 355, label: 'Solstice' },
  ];

  // Preset tilt values
  const tiltPresets = [
    { value: 0, label: 'No tilt', desc: 'No seasons' },
    { value: 23.45, label: 'Earth', desc: '23.45¬∞' },
    { value: 25.19, label: 'Mars', desc: '25.19¬∞' },
    { value: 82.23, label: 'Uranus', desc: '82.23¬∞' },
  ];

  return (
    <div style={{ 
      backgroundColor: '#1a1a1c', 
      padding: '20px', 
      borderRadius: '8px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#e9e9ea'
    }}>
      <h2 style={{ fontSize: '20px', margin: '0 0 16px 0' }}>
        Sun Altitude vs Time (UTC)
      </h2>

      <svg width={width} height={height} style={{ display: 'block' }}>
        <rect 
          x={padding.left} 
          y={padding.top} 
          width={graphWidth} 
          height={graphHeight} 
          fill="#27272a" 
          rx="4"
        />

        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
          <linearGradient id="sunGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#f4d03f" />
            <stop offset="100%" stopColor="#e67e22" />
          </linearGradient>
        </defs>

        {/* Night region */}
        <rect 
          x={padding.left} 
          y={horizonY} 
          width={graphWidth} 
          height={padding.top + graphHeight - horizonY}
          fill="rgba(35, 35, 52, 0.6)"
          clipPath="url(#graphClip)"
        />

        {/* Horizon */}
        <line 
          x1={padding.left} y1={horizonY} 
          x2={padding.left + graphWidth} y2={horizonY}
          stroke="#e67e22" strokeWidth="2" strokeDasharray="8,4"
        />
        <text x={padding.left + graphWidth + 5} y={horizonY + 4} fill="#e67e22" fontSize="12">
          horizon
        </text>

        {/* Grid - altitude */}
        {[-60, -30, 30, 60, 90].map(alt => {
          const y = padding.top + ((90 - alt) / 180) * graphHeight;
          return (
            <g key={alt}>
              <line 
                x1={padding.left} y1={y} 
                x2={padding.left + graphWidth} y2={y}
                stroke="#393941" strokeWidth="1"
              />
              <text x={padding.left - 8} y={y + 4} fill="#a1a1a8" fontSize="12" textAnchor="end">
                {alt}¬∞
              </text>
            </g>
          );
        })}

        {/* Grid - time */}
        {[0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          return (
            <g key={hour}>
              <line 
                x1={x} y1={padding.top} 
                x2={x} y2={padding.top + graphHeight}
                stroke="#393941" strokeWidth="1"
              />
              <text x={x} y={padding.top + graphHeight + 20} fill="#a1a1a8" fontSize="12" textAnchor="middle">
                {hour.toString().padStart(2, '0')}:00
              </text>
            </g>
          );
        })}

        {/* Equinox reference curve (if tilt > 0 and not at equinox) */}
        {axialTilt > 0 && Math.abs(declination) > 1 && (
          <path 
            d={equinoxPathD} 
            fill="none" 
            stroke="#a1a1a8" 
            strokeWidth="1"
            strokeDasharray="4,4"
            opacity="0.4"
            clipPath="url(#graphClip)"
          />
        )}

        {/* Sun path */}
        <path 
          d={pathD} 
          fill="none" 
          stroke="url(#sunGradient)" 
          strokeWidth="3"
          clipPath="url(#graphClip)"
        />

        {/* Sunrise/sunset markers */}
        {sunrisePoint && (
          <g>
            <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="7" fill="#e67e22" />
            <text x={sunrisePoint.x} y={sunrisePoint.y - 12} fill="#e67e22" fontSize="11" textAnchor="middle">‚Üë</text>
          </g>
        )}
        {sunsetPoint && (
          <g>
            <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="7" fill="#e25f73" />
            <text x={sunsetPoint.x} y={sunsetPoint.y - 12} fill="#e25f73" fontSize="11" textAnchor="middle">‚Üì</text>
          </g>
        )}

        {/* Max altitude marker */}
        {dayType !== 'polar-night' && (() => {
          const maxPoint = curveData.reduce((a, b) => a.altitude > b.altitude ? a : b);
          return (
            <g>
              <circle cx={maxPoint.x} cy={maxPoint.y} r="5" fill="#8c7ae6" />
              <line 
                x1={maxPoint.x} y1={maxPoint.y + 8} 
                x2={maxPoint.x} y2={horizonY - 2}
                stroke="#8c7ae6" strokeWidth="1" strokeDasharray="4,4" opacity="0.5"
              />
            </g>
          );
        })()}

        {/* Axis labels */}
        <text x={padding.left + graphWidth / 2} y={height - 10} fill="#e9e9ea" fontSize="14" textAnchor="middle">
          Time (UTC)
        </text>
        <text 
          x={15} y={padding.top + graphHeight / 2} 
          fill="#e9e9ea" fontSize="14" textAnchor="middle"
          transform={`rotate(-90, 15, ${padding.top + graphHeight / 2})`}
        >
          Altitude (¬∞)
        </text>

        {/* Legend for equinox line */}
        {axialTilt > 0 && Math.abs(declination) > 1 && (
          <g>
            <line x1={padding.left + 10} y1={padding.top + 15} x2={padding.left + 40} y2={padding.top + 15} 
              stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.5"/>
            <text x={padding.left + 45} y={padding.top + 18} fill="#a1a1a8" fontSize="10">equinox</text>
          </g>
        )}
      </svg>

      {/* Controls */}
      <div style={{ marginTop: '20px', display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
        {/* Longitude */}
        <div style={{ flex: 1, minWidth: '160px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Longitude: <span style={{ color: '#8c7ae6' }}>{longitude}¬∞</span>
          </label>
          <input 
            type="range" min="-180" max="180" value={longitude}
            onChange={(e) => setLongitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            <span>-180¬∞W</span><span>0¬∞</span><span>+180¬∞E</span>
          </div>
        </div>

        {/* Latitude */}
        <div style={{ flex: 1, minWidth: '160px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}¬∞</span>
          </label>
          <input 
            type="range" min="-90" max="90" value={latitude}
            onChange={(e) => setLatitude(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            <span>-90¬∞ S</span><span>0¬∞</span><span>+90¬∞ N</span>
          </div>
        </div>

        {/* Day of Year */}
        <div style={{ flex: 1.2, minWidth: '200px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Date: <span style={{ color: '#f4d03f' }}>{dateLabel}</span>
            <span style={{ color: '#a1a1a8', marginLeft: '8px', fontSize: '11px' }}>
              (Œ¥ = {declination >= 0 ? '+' : ''}{declination.toFixed(1)}¬∞)
            </span>
          </label>
          <input 
            type="range" min="1" max="365" value={dayOfYear}
            onChange={(e) => setDayOfYear(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#f4d03f' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            {keyDates.map(d => (
              <span 
                key={d.day} 
                style={{ cursor: 'pointer', color: Math.abs(dayOfYear - d.day) < 10 ? '#f4d03f' : '#a1a1a8' }}
                onClick={() => setDayOfYear(d.day)}
              >
                {d.label}
              </span>
            ))}
          </div>
        </div>

        {/* Axial Tilt */}
        <div style={{ flex: 1.3, minWidth: '220px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Axial Tilt: <span style={{ color: '#6ab0f3' }}>{axialTilt.toFixed(1)}¬∞</span>
          </label>
          <input 
            type="range" min="0" max="90" step="0.1" value={axialTilt}
            onChange={(e) => setAxialTilt(Number(e.target.value))}
            style={{ width: '100%', accentColor: '#6ab0f3' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', marginTop: '4px' }}>
            {tiltPresets.map(p => (
              <span 
                key={p.value}
                onClick={() => setAxialTilt(p.value)}
                style={{ 
                  cursor: 'pointer', 
                  padding: '2px 6px',
                  borderRadius: '4px',
                  backgroundColor: Math.abs(axialTilt - p.value) < 0.5 ? 'rgba(106, 176, 243, 0.2)' : 'transparent',
                  color: Math.abs(axialTilt - p.value) < 0.5 ? '#6ab0f3' : '#a1a1a8',
                  border: Math.abs(axialTilt - p.value) < 0.5 ? '1px solid #6ab0f3' : '1px solid transparent'
                }}
              >
                {p.label}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Info panel */}
      <div style={{ 
        marginTop: '20px', 
        padding: '16px', 
        backgroundColor: '#232334', 
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap', alignItems: 'center' }}>
          <div>
            <span style={{ color: '#a1a1a8' }}>Max altitude: </span>
            <span style={{ color: '#f4d03f' }}>{maxAltitude.toFixed(1)}¬∞</span>
          </div>
          <div>
            <span style={{ color: '#a1a1a8' }}>Daylight: </span>
            <span style={{ color: '#8c7ae6' }}>{daylightHours.toFixed(1)}h</span>
          </div>
          {dayType === 'normal' && sunrisePoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunrise: </span>
              <span style={{ color: '#e67e22' }}>
                {Math.floor(sunrisePoint.hour)}:{((sunrisePoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}
          {dayType === 'normal' && sunsetPoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunset: </span>
              <span style={{ color: '#e25f73' }}>
                {Math.floor(sunsetPoint.hour)}:{((sunsetPoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}

          {dayType !== 'normal' && (
            <div style={{ 
              padding: '4px 12px', 
              borderRadius: '4px',
              backgroundColor: dayType === 'midnight-sun' ? 'rgba(244, 208, 63, 0.2)' : 'rgba(35, 35, 52, 0.8)',
              border: `1px solid ${dayType === 'midnight-sun' ? '#f4d03f' : '#8c7ae6'}`
            }}>
              {dayType === 'midnight-sun' ? '‚òÄÔ∏è Midnight Sun' : 'üåë Polar Night'}
            </div>
          )}
        </div>

        {/* Tilt effects explanation */}
        <div style={{ 
          marginTop: '12px', 
          padding: '12px', 
          backgroundColor: '#27272a', 
          borderRadius: '4px',
          fontSize: '12px',
          color: '#a1a1a8'
        }}>
          <strong style={{ color: '#6ab0f3' }}>Axial Tilt Effects:</strong>
          <div style={{ marginTop: '8px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '8px' }}>
            <div>
              <span style={{ color: '#e9e9ea' }}>Polar circle:</span> {polarCircle.toFixed(1)}¬∞
              {Math.abs(latitude) > polarCircle && (
                <span style={{ color: '#e67e22', marginLeft: '6px' }}>‚Üê You're inside!</span>
              )}
            </div>
            <div>
              <span style={{ color: '#e9e9ea' }}>Max declination:</span> ¬±{axialTilt.toFixed(1)}¬∞
            </div>
            <div>
              <span style={{ color: '#e9e9ea' }}>Tropics at:</span> ¬±{axialTilt.toFixed(1)}¬∞
            </div>
          </div>

          {axialTilt === 0 && (
            <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#1a1a1c', borderRadius: '4px', borderLeft: '3px solid #6ab0f3' }}>
              <span style={{ fontStyle: 'italic' }}>
                No axial tilt = eternal equinox. Every day is 12 hours everywhere. No seasons.
              </span>
            </div>
          )}

          {axialTilt > 45 && (
            <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#1a1a1c', borderRadius: '4px', borderLeft: '3px solid #e67e22' }}>
              <span style={{ fontStyle: 'italic' }}>
                Extreme tilt! At solstice, the sun can be overhead at latitudes up to {axialTilt.toFixed(0)}¬∞. 
                {axialTilt > 66 && ' Most of the planet experiences midnight sun or polar night.'}
              </span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

