{"language": "javascript", "code": "import { useState } from 'react';

const SunPositionViz = () => {
  const [latitude, setLatitude] = useState(45);
  const [dayOfYear, setDayOfYear] = useState(172);
  const [axialTilt, setAxialTilt] = useState(23.45);
  const [viewMode, setViewMode] = useState('day'); // 'day' or 'year'

  const width = 700;
  const height = 320;
  const padding = { top: 40, right: 60, bottom: 60, left: 60 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  // Solar declination from day of year and axial tilt
  const getDeclination = (day) => axialTilt * Math.sin((2 * Math.PI / 365) * (day - 81));
  const declination = getDeclination(dayOfYear);

  // Date label
  const getDateLabel = (day) => {
    const date = new Date(2024, 0, 1);
    date.setDate(day);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };
  const dateLabel = getDateLabel(dayOfYear);

  // Compute altitude for given hour and declination
  const getAltitude = (hour, decl) => {
    const decRad = (decl * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;
    const hourAngle = ((hour - 12) * 15 * Math.PI) / 180;

    const sinAltitude = 
      Math.sin(latRad) * Math.sin(decRad) + 
      Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);

    return (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;
  };

  // Compute daily curve
  const computeDayCurve = (decl) => {
    const points = [];
    for (let i = 0; i <= 240; i++) {
      const hour = i / 10;
      const altitude = getAltitude(hour, decl);
      const x = padding.left + (hour / 24) * graphWidth;
      const y = padding.top + ((90 - altitude) / 180) * graphHeight;
      points.push({ x, y, hour, altitude });
    }
    return points;
  };

  // Compute yearly curve (max altitude per day)
  const computeYearCurve = () => {
    const points = [];
    for (let day = 1; day <= 365; day++) {
      const decl = getDeclination(day);
      const maxAlt = getAltitude(12, decl); // Noon altitude
      const x = padding.left + ((day - 1) / 364) * graphWidth;
      const y = padding.top + ((90 - maxAlt) / 180) * graphHeight;
      points.push({ x, y, day, altitude: maxAlt });
    }
    return points;
  };

  const curveData = viewMode === 'day' ? computeDayCurve(declination) : computeYearCurve();
  const equinoxCurve = viewMode === 'day' ? computeDayCurve(0) : null;

  const pathD = curveData.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ');
  const equinoxPathD = equinoxCurve ? equinoxCurve.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ') : '';

  const horizonY = padding.top + (90 / 180) * graphHeight;

  const maxAltitude = Math.max(...curveData.map(p => p.altitude));
  const minAltitude = Math.min(...curveData.map(p => p.altitude));

  const dayType = viewMode === 'day' 
    ? (minAltitude > 0 ? 'midnight-sun' : maxAltitude < 0 ? 'polar-night' : 'normal')
    : 'normal';

  // Find sunrise/sunset for day view
  const sunrisePoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0) : null;
  const sunsetPoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0) : null;

  const daylightHours = dayType === 'midnight-sun' ? 24 
    : dayType === 'polar-night' ? 0 
    : viewMode === 'day' ? (curveData.filter(p => p.altitude >= 0).length / curveData.length) * 24
    : null;

  const polarCircle = 90 - axialTilt;

  const keyDates = [
    { day: 1, label: 'Jan 1' },
    { day: 80, label: 'Equinox' },
    { day: 172, label: 'Solstice' },
    { day: 266, label: 'Equinox' },
    { day: 355, label: 'Solstice' },
  ];

  const tiltPresets = [
    { value: 0, label: 'No tilt' },
    { value: 23.45, label: 'Earth' },
    { value: 25.19, label: 'Mars' },
    { value: 82.23, label: 'Uranus' },
  ];

  const maxPoint = curveData.reduce((a, b) => a.altitude > b.altitude ? a : b);

  // Current day marker for year view
  const currentDayX = viewMode === 'year' ? padding.left + ((dayOfYear - 1) / 364) * graphWidth : null;
  const currentDayPoint = viewMode === 'year' ? curveData.find(p => p.day === dayOfYear) : null;

  return (
    <div style={{ 
      backgroundColor: '#1a1a1c', 
      padding: '20px', 
      borderRadius: '8px',
      fontFamily: 'system-ui, -apple-system, sans-serif',
      color: '#e9e9ea'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}>
        <h2 style={{ fontSize: '20px', margin: 0 }}>
          Sun Altitude — {viewMode === 'day' ? 'One Day' : 'Full Year'}
        </h2>

        {/* View toggle */}
        <div style={{ display: 'flex', gap: '4px', backgroundColor: '#27272a', padding: '4px', borderRadius: '8px' }}>
          <button
            onClick={() => setViewMode('day')}
            style={{
              padding: '6px 12px',
              borderRadius: '6px',
              border: 'none',
              cursor: 'pointer',
              fontSize: '13px',
              backgroundColor: viewMode === 'day' ? '#8c7ae6' : 'transparent',
              color: viewMode === 'day' ? '#1a1a1c' : '#a1a1a8',
              transition: 'all 0.2s'
            }}
          >
            24 Hours
          </button>
          <button
            onClick={() => setViewMode('year')}
            style={{
              padding: '6px 12px',
              borderRadius: '6px',
              border: 'none',
              cursor: 'pointer',
              fontSize: '13px',
              backgroundColor: viewMode === 'year' ? '#8c7ae6' : 'transparent',
              color: viewMode === 'year' ? '#1a1a1c' : '#a1a1a8',
              transition: 'all 0.2s'
            }}
          >
            365 Days
          </button>
        </div>
      </div>

      <svg width={width} height={height} style={{ display: 'block' }}>
        <rect 
          x={padding.left} y={padding.top} 
          width={graphWidth} height={graphHeight} 
          fill="#27272a" rx="4"
        />

        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
          <linearGradient id="sunGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#f4d03f" />
            <stop offset="100%" stopColor="#e67e22" />
          </linearGradient>
        </defs>

        {/* Night region */}
        <rect 
          x={padding.left} y={horizonY} 
          width={graphWidth} height={padding.top + graphHeight - horizonY}
          fill="rgba(35, 35, 52, 0.6)"
          clipPath="url(#graphClip)"
        />

        {/* Horizon */}
        <line 
          x1={padding.left} y1={horizonY} 
          x2={padding.left + graphWidth} y2={horizonY}
          stroke="#e67e22" strokeWidth="2" strokeDasharray="8,4"
        />
        <text x={padding.left + graphWidth + 5} y={horizonY + 4} fill="#e67e22" fontSize="12">
          horizon
        </text>

        {/* Grid - altitude */}
        {[-60, -30, 30, 60, 90].map(alt => {
          const y = padding.top + ((90 - alt) / 180) * graphHeight;
          return (
            <g key={alt}>
              <line x1={padding.left} y1={y} x2={padding.left + graphWidth} y2={y} stroke="#393941" strokeWidth="1" />
              <text x={padding.left - 8} y={y + 4} fill="#a1a1a8" fontSize="12" textAnchor="end">{alt}°</text>
            </g>
          );
        })}

        {/* Grid - time (day view) */}
        {viewMode === 'day' && [0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          const label = hour === 0 ? 'midnight' : hour === 12 ? 'noon' : `${hour}h`;
          return (
            <g key={hour}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text x={x} y={padding.top + graphHeight + 20} fill="#a1a1a8" fontSize="12" textAnchor="middle">{label}</text>
            </g>
          );
        })}

        {/* Grid - months (year view) */}
        {viewMode === 'year' && [
          { day: 1, label: 'Jan' },
          { day: 32, label: 'Feb' },
          { day: 60, label: 'Mar' },
          { day: 91, label: 'Apr' },
          { day: 121, label: 'May' },
          { day: 152, label: 'Jun' },
          { day: 182, label: 'Jul' },
          { day: 213, label: 'Aug' },
          { day: 244, label: 'Sep' },
          { day: 274, label: 'Oct' },
          { day: 305, label: 'Nov' },
          { day: 335, label: 'Dec' },
        ].map(m => {
          const x = padding.left + ((m.day - 1) / 364) * graphWidth;
          return (
            <g key={m.day}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text x={x + 12} y={padding.top + graphHeight + 20} fill="#a1a1a8" fontSize="11" textAnchor="middle">{m.label}</text>
            </g>
          );
        })}

        {/* Equinox reference (day view only) */}
        {viewMode === 'day' && axialTilt > 0 && Math.abs(declination) > 1 && (
          <path d={equinoxPathD} fill="none" stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.4" clipPath="url(#graphClip)" />
        )}

        {/* Sun path */}
        <path d={pathD} fill="none" stroke="url(#sunGradient)" strokeWidth="3" clipPath="url(#graphClip)" />

        {/* Sunrise/sunset (day view) */}
        {viewMode === 'day' && sunrisePoint && (
          <g>
            <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="7" fill="#e67e22" />
            <text x={sunrisePoint.x} y={sunrisePoint.y - 12} fill="#e67e22" fontSize="11" textAnchor="middle">↑</text>
          </g>
        )}
        {viewMode === 'day' && sunsetPoint && (
          <g>
            <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="7" fill="#e25f73" />
            <text x={sunsetPoint.x} y={sunsetPoint.y - 12} fill="#e25f73" fontSize="11" textAnchor="middle">↓</text>
          </g>
        )}

        {/* Current day marker (year view) */}
        {viewMode === 'year' && currentDayPoint && (
          <g>
            <line x1={currentDayX} y1={padding.top} x2={currentDayX} y2={padding.top + graphHeight} stroke="#8c7ae6" strokeWidth="2" strokeDasharray="4,4" opacity="0.6" />
            <circle cx={currentDayPoint.x} cy={currentDayPoint.y} r="6" fill="#8c7ae6" />
            <text x={currentDayPoint.x} y={padding.top - 8} fill="#8c7ae6" fontSize="11" textAnchor="middle">{dateLabel}</text>
          </g>
        )}

        {/* Max altitude marker (always shown) */}
        {viewMode === 'day' && (
          <g>
            <circle cx={maxPoint.x} cy={maxPoint.y} r="5" fill="#8c7ae6" />
            <line x1={maxPoint.x} y1={maxPoint.y + 8} x2={maxPoint.x} y2={horizonY - 2} stroke="#8c7ae6" strokeWidth="1" strokeDasharray="4,4" opacity="0.5" />
          </g>
        )}

        {/* Axis labels */}
        <text x={padding.left + graphWidth / 2} y={height - 10} fill="#e9e9ea" fontSize="14" textAnchor="middle">
          {viewMode === 'day' ? 'Local Solar Time' : 'Day of Year'}
        </text>
        <text x={15} y={padding.top + graphHeight / 2} fill="#e9e9ea" fontSize="14" textAnchor="middle" transform={`rotate(-90, 15, ${padding.top + graphHeight / 2})`}>
          {viewMode === 'day' ? 'Altitude (°)' : 'Max Altitude (°)'}
        </text>

        {/* Legend (day view) */}
        {viewMode === 'day' && axialTilt > 0 && Math.abs(declination) > 1 && (
          <g>
            <line x1={padding.left + 10} y1={padding.top + 15} x2={padding.left + 40} y2={padding.top + 15} stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.5"/>
            <text x={padding.left + 45} y={padding.top + 18} fill="#a1a1a8" fontSize="10">equinox</text>
          </g>
        )}
      </svg>

      {/* Controls */}
      <div style={{ marginTop: '20px', display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
        {/* Latitude */}
        <div style={{ flex: 1, minWidth: '180px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}°</span>
          </label>
          <input 
            type="range" min={-90} max={90} value={latitude}
            onChange={(e) => setLatitude(parseInt(e.target.value, 10))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            <span>-90° S</span><span>0°</span><span>+90° N</span>
          </div>
        </div>

        {/* Day of Year */}
        <div style={{ flex: 1.2, minWidth: '220px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Date: <span style={{ color: '#f4d03f' }}>{dateLabel}</span>
            <span style={{ color: '#a1a1a8', marginLeft: '8px', fontSize: '11px' }}>
              (δ = {declination >= 0 ? '+' : ''}{declination.toFixed(1)}°)
            </span>
          </label>
          <input 
            type="range" min={1} max={365} value={dayOfYear}
            onChange={(e) => setDayOfYear(parseInt(e.target.value, 10))}
            style={{ width: '100%', accentColor: '#f4d03f' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            {keyDates.map(d => (
              <span 
                key={d.day} 
                style={{ cursor: 'pointer', color: Math.abs(dayOfYear - d.day) < 10 ? '#f4d03f' : '#a1a1a8' }}
                onClick={() => setDayOfYear(d.day)}
              >
                {d.label}
              </span>
            ))}
          </div>
        </div>

        {/* Axial Tilt */}
        <div style={{ flex: 1.3, minWidth: '240px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Axial Tilt: <span style={{ color: '#6ab0f3' }}>{axialTilt.toFixed(1)}°</span>
          </label>
          <input 
            type="range" min={0} max={90} step={0.1} value={axialTilt}
            onChange={(e) => setAxialTilt(parseFloat(e.target.value))}
            style={{ width: '100%', accentColor: '#6ab0f3' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', marginTop: '4px' }}>
            {tiltPresets.map(p => (
              <span 
                key={p.value}
                onClick={() => setAxialTilt(p.value)}
                style={{ 
                  cursor: 'pointer', 
                  padding: '2px 6px',
                  borderRadius: '4px',
                  backgroundColor: Math.abs(axialTilt - p.value) < 0.5 ? 'rgba(106, 176, 243, 0.2)' : 'transparent',
                  color: Math.abs(axialTilt - p.value) < 0.5 ? '#6ab0f3' : '#a1a1a8',
                  border: Math.abs(axialTilt - p.value) < 0.5 ? '1px solid #6ab0f3' : '1px solid transparent'
                }}
              >
                {p.label}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Info panel */}
      <div style={{ 
        marginTop: '20px', 
        padding: '16px', 
        backgroundColor: '#232334', 
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap', alignItems: 'center' }}>
          <div>
            <span style={{ color: '#a1a1a8' }}>{viewMode === 'day' ? 'Max altitude' : 'Peak (solstice)'}: </span>
            <span style={{ color: '#f4d03f' }}>{maxAltitude.toFixed(1)}°</span>
          </div>
          {viewMode === 'day' && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Daylight: </span>
              <span style={{ color: '#8c7ae6' }}>{daylightHours.toFixed(1)}h</span>
            </div>
          )}
          {viewMode === 'day' && dayType === 'normal' && sunrisePoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunrise: </span>
              <span style={{ color: '#e67e22' }}>
                {Math.floor(sunrisePoint.hour)}:{((sunrisePoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}
          {viewMode === 'day' && dayType === 'normal' && sunsetPoint && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Sunset: </span>
              <span style={{ color: '#e25f73' }}>
                {Math.floor(sunsetPoint.hour)}:{((sunsetPoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
              </span>
            </div>
          )}

          {viewMode === 'day' && dayType !== 'normal' && (
            <div style={{ 
              padding: '4px 12px', 
              borderRadius: '4px',
              backgroundColor: dayType === 'midnight-sun' ? 'rgba(244, 208, 63, 0.2)' : 'rgba(35, 35, 52, 0.8)',
              border: `1px solid ${dayType === 'midnight-sun' ? '#f4d03f' : '#8c7ae6'}`
            }}>
              {dayType === 'midnight-sun' ? '☀️ Midnight Sun' : '🌑 Polar Night'}
            </div>
          )}

          {viewMode === 'year' && (
            <div>
              <span style={{ color: '#a1a1a8' }}>Min (solstice): </span>
              <span style={{ color: '#6ab0f3' }}>{minAltitude.toFixed(1)}°</span>
            </div>
          )}
        </div>

        {/* Tilt info */}
        <div style={{ 
          marginTop: '12px', 
          padding: '12px', 
          backgroundColor: '#27272a', 
          borderRadius: '4px',
          fontSize: '12px',
          color: '#a1a1a8',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
          gap: '8px'
        }}>
          <div>
            <span style={{ color: '#e9e9ea' }}>Polar circles:</span> ±{polarCircle.toFixed(1)}°
            {Math.abs(latitude) > polarCircle && <span style={{ color: '#e67e22' }}> ← inside!</span>}
          </div>
          <div>
            <span style={{ color: '#e9e9ea' }}>Tropics:</span> ±{axialTilt.toFixed(1)}°
          </div>
          <div>
            <span style={{ color: '#e9e9ea' }}>Declination range:</span> ±{axialTilt.toFixed(1)}°
          </div>
        </div>

        {axialTilt === 0 && (
          <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#1a1a1c', borderRadius: '4px', borderLeft: '3px solid #6ab0f3', fontSize: '12px', fontStyle: 'italic' }}>
            No axial tilt = eternal equinox. 12h days everywhere. No seasons.
          </div>
        )}

        {axialTilt > 45 && (
          <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#1a1a1c', borderRadius: '4px', borderLeft: '3px solid #e67e22', fontSize: '12px', fontStyle: 'italic' }}>
            Extreme tilt! Sun can be overhead up to {axialTilt.toFixed(0)}° latitude.
            {axialTilt > 66 && ' Most of the planet experiences polar extremes.'}
          </div>
        )}
      </div>
    </div>
  );
};

export default SunPositionViz;
