import { useState, useMemo, useRef, useEffect } from 'react';

const SunPositionViz = () => {
  const [latitude, setLatitude] = useState(45);
  const [minuteOfYear, setMinuteOfYear] = useState(172 * 24 * 60 + 12 * 60);
  const [axialTilt, setAxialTilt] = useState(23.45);
  const [viewMode, setViewMode] = useState('day');
  const [yAxisMode, setYAxisMode] = useState('dynamic'); // 'dynamic', 'fixed', 'wide'
  const [containerWidth, setContainerWidth] = useState(800);

  const containerRef = useRef(null);

  useEffect(() => {
    const updateWidth = () => {
      if (containerRef.current) {
        setContainerWidth(containerRef.current.offsetWidth);
      }
    };
    updateWidth();
    window.addEventListener('resize', updateWidth);
    return () => window.removeEventListener('resize', updateWidth);
  }, []);

  const width = containerWidth;
  const height = 320;
  const padding = { top: 40, right: 60, bottom: 60, left: 60 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  const totalMinutesInYear = 365 * 24 * 60;

  const dayOfYear = Math.floor(minuteOfYear / (24 * 60)) + 1;
  const hourOfDay = (minuteOfYear % (24 * 60)) / 60;

  const getDeclination = (day) => axialTilt * Math.sin((2 * Math.PI / 365) * (day - 81));
  const declination = getDeclination(dayOfYear);

  const getDateTimeLabel = () => {
    const date = new Date(2024, 0, 1);
    date.setDate(dayOfYear);
    const dateStr = date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const hours = Math.floor(hourOfDay);
    const mins = Math.round((hourOfDay % 1) * 60);
    return `${dateStr}, ${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
  };

  const getAltitude = (hour, decl) => {
    const decRad = (decl * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;
    const hourAngle = ((hour - 12) * 15 * Math.PI) / 180;
    const sinAltitude = 
      Math.sin(latRad) * Math.sin(decRad) + 
      Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);
    return (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;
  };

  const getAltitudeAtMinute = (minute) => {
    const day = Math.floor(minute / (24 * 60)) + 1;
    const hour = (minute % (24 * 60)) / 60;
    const decl = getDeclination(day);
    return getAltitude(hour, decl);
  };

  const computeDayAltitudes = (decl) => {
    const points = [];
    for (let i = 0; i <= 240; i++) {
      const hour = i / 10;
      const altitude = getAltitude(hour, decl);
      points.push({ hour, altitude });
    }
    return points;
  };

  const yearAltitudes = useMemo(() => {
    const points = [];
    const step = 120;
    for (let minute = 0; minute < totalMinutesInYear; minute += step) {
      const altitude = getAltitudeAtMinute(minute);
      points.push({ minute, altitude });
    }
    return points;
  }, [latitude, axialTilt]);

  const dayAltitudes = useMemo(() => {
    return computeDayAltitudes(declination);
  }, [latitude, declination]);

  const equinoxAltitudes = useMemo(() => {
    return computeDayAltitudes(0);
  }, [latitude]);

  const rawData = viewMode === 'day' ? dayAltitudes : yearAltitudes;

  // Calculate Y-axis range
  const { yMin, yMax, yRange } = useMemo(() => {
    if (yAxisMode === 'fixed') {
      return { yMin: -90, yMax: 90, yRange: 180 };
    }

    if (yAxisMode === 'wide') {
      return { yMin: -135, yMax: 135, yRange: 270 };
    }

    // Dynamic mode
    const altitudes = rawData.map(p => p.altitude);
    const dataMin = Math.min(...altitudes);
    const dataMax = Math.max(...altitudes);

    const range = dataMax - dataMin;
    const paddingAmt = Math.max(range * 0.1, 5);

    let niceMin = Math.floor((dataMin - paddingAmt) / 10) * 10;
    let niceMax = Math.ceil((dataMax + paddingAmt) / 10) * 10;

    if (dataMin < 0 && dataMax > 0) {
      niceMin = Math.min(niceMin, -10);
      niceMax = Math.max(niceMax, 10);
    }

    // For dynamic, still clamp to physical limits
    niceMin = Math.max(niceMin, -90);
    niceMax = Math.min(niceMax, 90);

    return { 
      yMin: niceMin, 
      yMax: niceMax, 
      yRange: niceMax - niceMin 
    };
  }, [rawData, yAxisMode]);

  const altToY = (alt) => padding.top + ((yMax - alt) / yRange) * graphHeight;

  const curveData = useMemo(() => {
    return rawData.map((p) => ({
      ...p,
      x: padding.left + ((viewMode === 'day' ? p.hour / 24 : p.minute / totalMinutesInYear)) * graphWidth,
      y: altToY(p.altitude)
    }));
  }, [rawData, graphWidth, yMax, yRange, viewMode]);

  const equinoxCurve = useMemo(() => {
    if (viewMode !== 'day') return null;
    return equinoxAltitudes.map((p) => ({
      ...p,
      x: padding.left + (p.hour / 24) * graphWidth,
      y: altToY(p.altitude)
    }));
  }, [equinoxAltitudes, graphWidth, yMax, yRange, viewMode]);

  const pathD = curveData.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ');
  const equinoxPathD = equinoxCurve ? equinoxCurve.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ') : '';

  const horizonY = altToY(0);
  const horizonVisible = yMin <= 0 && yMax >= 0;

  // Physical limits lines (for wide mode)
  const zenithY = altToY(90);
  const nadirY = altToY(-90);
  const showPhysicalLimits = yAxisMode === 'wide';

  const currentAltitude = getAltitudeAtMinute(minuteOfYear);
  const currentX = viewMode === 'day' 
    ? padding.left + (hourOfDay / 24) * graphWidth
    : padding.left + (minuteOfYear / totalMinutesInYear) * graphWidth;
  const currentY = altToY(currentAltitude);

  const dayType = viewMode === 'day' ? (() => {
    const minAlt = Math.min(...rawData.map(p => p.altitude));
    const maxAlt = Math.max(...rawData.map(p => p.altitude));
    return minAlt > 0 ? 'midnight-sun' : maxAlt < 0 ? 'polar-night' : 'normal';
  })() : 'normal';

  const sunrisePoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0) : null;
  const sunsetPoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0) : null;

  const daylightHours = viewMode === 'day' ? (
    dayType === 'midnight-sun' ? 24 
    : dayType === 'polar-night' ? 0 
    : (curveData.filter(p => p.altitude >= 0).length / curveData.length) * 24
  ) : null;

  const polarCircle = 90 - axialTilt;

  const tiltPresets = [
    { value: 0, label: 'No tilt' },
    { value: 23.45, label: 'Earth' },
    { value: 25.19, label: 'Mars' },
    { value: 82.23, label: 'Uranus' },
  ];

  const datePresets = [
    { minute: 0, label: 'Jan 1' },
    { minute: 79 * 24 * 60 + 12 * 60, label: 'Spring Eq' },
    { minute: 171 * 24 * 60 + 12 * 60, label: 'Summer Sol' },
    { minute: 265 * 24 * 60 + 12 * 60, label: 'Fall Eq' },
    { minute: 354 * 24 * 60 + 12 * 60, label: 'Winter Sol' },
  ];

  const yGridLines = useMemo(() => {
    const lines = [];
    const step = yRange <= 60 ? 10 : yRange <= 120 ? 20 : 30;
    for (let alt = Math.ceil(yMin / step) * step; alt <= yMax; alt += step) {
      lines.push(alt);
    }
    return lines;
  }, [yMin, yMax, yRange]);

  return (
    <div 
      ref={containerRef}
      style={{ 
        backgroundColor: '#1a1a1c', 
        padding: '20px', 
        borderRadius: '8px',
        fontFamily: 'system-ui, -apple-system, sans-serif',
        color: '#e9e9ea',
        width: '100%',
        boxSizing: 'border-box'
      }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px', flexWrap: 'wrap', gap: '12px' }}>
        <h2 style={{ fontSize: '20px', margin: 0 }}>
          Sun Altitude ‚Äî {viewMode === 'day' ? 'One Day' : 'Full Year'}
        </h2>

        <div style={{ display: 'flex', gap: '12px', flexWrap: 'wrap' }}>
          <div style={{ display: 'flex', gap: '4px', backgroundColor: '#27272a', padding: '4px', borderRadius: '8px' }}>
            <button
              onClick={() => setYAxisMode('dynamic')}
              style={{
                padding: '6px 10px',
                borderRadius: '6px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '12px',
                backgroundColor: yAxisMode === 'dynamic' ? '#6ab0f3' : 'transparent',
                color: yAxisMode === 'dynamic' ? '#1a1a1c' : '#a1a1a8',
              }}
            >
              Auto
            </button>
            <button
              onClick={() => setYAxisMode('fixed')}
              style={{
                padding: '6px 10px',
                borderRadius: '6px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '12px',
                backgroundColor: yAxisMode === 'fixed' ? '#6ab0f3' : 'transparent',
                color: yAxisMode === 'fixed' ? '#1a1a1c' : '#a1a1a8',
              }}
            >
              ¬±90¬∞
            </button>
            <button
              onClick={() => setYAxisMode('wide')}
              style={{
                padding: '6px 10px',
                borderRadius: '6px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '12px',
                backgroundColor: yAxisMode === 'wide' ? '#6ab0f3' : 'transparent',
                color: yAxisMode === 'wide' ? '#1a1a1c' : '#a1a1a8',
              }}
            >
              ¬±135¬∞
            </button>
          </div>

          <div style={{ display: 'flex', gap: '4px', backgroundColor: '#27272a', padding: '4px', borderRadius: '8px' }}>
            <button
              onClick={() => setViewMode('day')}
              style={{
                padding: '6px 12px',
                borderRadius: '6px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '13px',
                backgroundColor: viewMode === 'day' ? '#8c7ae6' : 'transparent',
                color: viewMode === 'day' ? '#1a1a1c' : '#a1a1a8',
              }}
            >
              24 Hours
            </button>
            <button
              onClick={() => setViewMode('year')}
              style={{
                padding: '6px 12px',
                borderRadius: '6px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '13px',
                backgroundColor: viewMode === 'year' ? '#8c7ae6' : 'transparent',
                color: viewMode === 'year' ? '#1a1a1c' : '#a1a1a8',
              }}
            >
              365 Days
            </button>
          </div>
        </div>
      </div>

      {/* Date/Time slider */}
      <div style={{ marginBottom: '16px', padding: '16px', backgroundColor: '#232334', borderRadius: '8px' }}>
        <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
          Date & Time: <span style={{ color: '#f4d03f' }}>{getDateTimeLabel()}</span>
          <span style={{ color: '#a1a1a8', marginLeft: '12px', fontSize: '11px' }}>
            (Œ¥ = {declination >= 0 ? '+' : ''}{declination.toFixed(1)}¬∞)
          </span>
          <span style={{ color: '#a1a1a8', marginLeft: '12px', fontSize: '11px' }}>
            altitude = <span style={{ color: currentAltitude >= 0 ? '#f4d03f' : '#6ab0f3' }}>{currentAltitude.toFixed(1)}¬∞</span>
          </span>
        </label>
        <input 
          type="range" 
          min={0} 
          max={totalMinutesInYear - 1} 
          step={15}
          value={minuteOfYear}
          onChange={(e) => setMinuteOfYear(parseInt(e.target.value, 10))}
          style={{ width: '100%', accentColor: '#f4d03f' }}
        />
        <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8', marginTop: '4px' }}>
          {datePresets.map(p => (
            <span 
              key={p.label} 
              style={{ cursor: 'pointer', color: Math.abs(minuteOfYear - p.minute) < 24 * 60 ? '#f4d03f' : '#a1a1a8' }}
              onClick={() => setMinuteOfYear(p.minute)}
            >
              {p.label}
            </span>
          ))}
        </div>
      </div>

      <svg width={width} height={height} style={{ display: 'block' }}>
        <rect 
          x={padding.left} y={padding.top} 
          width={graphWidth} height={graphHeight} 
          fill="#27272a" rx="4"
        />

        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
          <linearGradient id="sunGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#f4d03f" />
            <stop offset="100%" stopColor="#e67e22" />
          </linearGradient>
        </defs>

        {/* Physical limits zone (wide mode) */}
        {showPhysicalLimits && (
          <>
            {/* Above zenith - impossible zone */}
            <rect 
              x={padding.left} y={padding.top} 
              width={graphWidth} height={zenithY - padding.top}
              fill="rgba(226, 95, 115, 0.15)"
              clipPath="url(#graphClip)"
            />
            {/* Below nadir - impossible zone */}
            <rect 
              x={padding.left} y={nadirY} 
              width={graphWidth} height={padding.top + graphHeight - nadirY}
              fill="rgba(226, 95, 115, 0.15)"
              clipPath="url(#graphClip)"
            />
          </>
        )}

        {/* Night region */}
        {horizonVisible && (
          <rect 
            x={padding.left} y={horizonY} 
            width={graphWidth} height={Math.min(nadirY, padding.top + graphHeight) - horizonY}
            fill="rgba(35, 35, 52, 0.6)"
            clipPath="url(#graphClip)"
          />
        )}

        {/* Physical limit lines (wide mode) */}
        {showPhysicalLimits && (
          <>
            <line 
              x1={padding.left} y1={zenithY} 
              x2={padding.left + graphWidth} y2={zenithY}
              stroke="#e25f73" strokeWidth="1" strokeDasharray="4,4" opacity="0.7"
            />
            <text x={padding.left + graphWidth + 5} y={zenithY + 4} fill="#e25f73" fontSize="10">
              zenith
            </text>
            <line 
              x1={padding.left} y1={nadirY} 
              x2={padding.left + graphWidth} y2={nadirY}
              stroke="#e25f73" strokeWidth="1" strokeDasharray="4,4" opacity="0.7"
            />
            <text x={padding.left + graphWidth + 5} y={nadirY + 4} fill="#e25f73" fontSize="10">
              nadir
            </text>
          </>
        )}

        {/* Horizon */}
        {horizonVisible && (
          <>
            <line 
              x1={padding.left} y1={horizonY} 
              x2={padding.left + graphWidth} y2={horizonY}
              stroke="#e67e22" strokeWidth="2" strokeDasharray="8,4"
            />
            <text x={padding.left + graphWidth + 5} y={horizonY + 4} fill="#e67e22" fontSize="12">
              horizon
            </text>
          </>
        )}

        {/* Grid - altitude */}
        {yGridLines.map(alt => {
          const y = altToY(alt);
          if (alt === 0 && horizonVisible) return null;
          if (showPhysicalLimits && (alt === 90 || alt === -90)) return null;
          return (
            <g key={alt}>
              <line x1={padding.left} y1={y} x2={padding.left + graphWidth} y2={y} stroke="#393941" strokeWidth="1" />
              <text x={padding.left - 8} y={y + 4} fill="#a1a1a8" fontSize="12" textAnchor="end">{alt}¬∞</text>
            </g>
          );
        })}

        {/* Grid - time (day view) */}
        {viewMode === 'day' && [0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          const label = hour === 0 ? 'midnight' : hour === 12 ? 'noon' : `${hour}h`;
          return (
            <g key={hour}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text x={x} y={padding.top + graphHeight + 20} fill="#a1a1a8" fontSize="12" textAnchor="middle">{label}</text>
            </g>
          );
        })}

        {/* Grid - months (year view) */}
        {viewMode === 'year' && [
          { day: 1, label: 'Jan' }, { day: 32, label: 'Feb' }, { day: 60, label: 'Mar' },
          { day: 91, label: 'Apr' }, { day: 121, label: 'May' }, { day: 152, label: 'Jun' },
          { day: 182, label: 'Jul' }, { day: 213, label: 'Aug' }, { day: 244, label: 'Sep' },
          { day: 274, label: 'Oct' }, { day: 305, label: 'Nov' }, { day: 335, label: 'Dec' },
        ].map(m => {
          const x = padding.left + ((m.day - 1) * 24 * 60 / totalMinutesInYear) * graphWidth;
          return (
            <g key={m.day}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text x={x + 12} y={padding.top + graphHeight + 20} fill="#a1a1a8" fontSize="11" textAnchor="middle">{m.label}</text>
            </g>
          );
        })}

        {/* Equinox reference (day view) */}
        {viewMode === 'day' && axialTilt > 0 && Math.abs(declination) > 1 && equinoxCurve && (
          <path d={equinoxPathD} fill="none" stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.4" clipPath="url(#graphClip)" />
        )}

        {/* Sun path */}
        <path d={pathD} fill="none" stroke="url(#sunGradient)" strokeWidth={viewMode === 'day' ? 3 : 1.5} clipPath="url(#graphClip)" />

        {/* Sunrise/sunset (day view) */}
        {viewMode === 'day' && sunrisePoint && (
          <g>
            <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="6" fill="#e67e22" />
            <text x={sunrisePoint.x} y={sunrisePoint.y - 10} fill="#e67e22" fontSize="10" textAnchor="middle">‚Üë</text>
          </g>
        )}
        {viewMode === 'day' && sunsetPoint && (
          <g>
            <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="6" fill="#e25f73" />
            <text x={sunsetPoint.x} y={sunsetPoint.y - 10} fill="#e25f73" fontSize="10" textAnchor="middle">‚Üì</text>
          </g>
        )}

        {/* Current position marker */}
        <g>
          <circle cx={currentX} cy={currentY} r="7" fill="#8c7ae6" stroke="#1a1a1c" strokeWidth="2" />
        </g>

        {/* Axis labels */}
        <text x={padding.left + graphWidth / 2} y={height - 10} fill="#e9e9ea" fontSize="14" textAnchor="middle">
          {viewMode === 'day' ? 'Local Solar Time' : 'Month'}
        </text>
        <text x={15} y={padding.top + graphHeight / 2} fill="#e9e9ea" fontSize="14" textAnchor="middle" transform={`rotate(-90, 15, ${padding.top + graphHeight / 2})`}>
          Altitude (¬∞)
        </text>

        {/* Legend */}
        {viewMode === 'day' && axialTilt > 0 && Math.abs(declination) > 1 && (
          <g>
            <line x1={padding.left + 10} y1={padding.top + 15} x2={padding.left + 40} y2={padding.top + 15} stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.5"/>
            <text x={padding.left + 45} y={padding.top + 18} fill="#a1a1a8" fontSize="10">equinox</text>
          </g>
        )}
      </svg>

      {/* Bottom controls */}
      <div style={{ marginTop: '20px', display: 'flex', gap: '24px', flexWrap: 'wrap' }}>
        <div style={{ flex: 1, minWidth: '200px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}¬∞</span>
          </label>
          <input 
            type="range" min={-90} max={90} value={latitude}
            onChange={(e) => setLatitude(parseInt(e.target.value, 10))}
            style={{ width: '100%', accentColor: '#8c7ae6' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#a1a1a8' }}>
            <span>-90¬∞ S</span><span>0¬∞</span><span>+90¬∞ N</span>
          </div>
        </div>

        <div style={{ flex: 1, minWidth: '240px' }}>
          <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px' }}>
            Axial Tilt: <span style={{ color: '#6ab0f3' }}>{axialTilt.toFixed(1)}¬∞</span>
          </label>
          <input 
            type="range" min={0} max={90} step={0.1} value={axialTilt}
            onChange={(e) => setAxialTilt(parseFloat(e.target.value))}
            style={{ width: '100%', accentColor: '#6ab0f3' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', marginTop: '4px' }}>
            {tiltPresets.map(p => (
              <span 
                key={p.value}
                onClick={() => setAxialTilt(p.value)}
                style={{ 
                  cursor: 'pointer', 
                  padding: '2px 6px',
                  borderRadius: '4px',
                  backgroundColor: Math.abs(axialTilt - p.value) < 0.5 ? 'rgba(106, 176, 243, 0.2)' : 'transparent',
                  color: Math.abs(axialTilt - p.value) < 0.5 ? '#6ab0f3' : '#a1a1a8',
                  border: Math.abs(axialTilt - p.value) < 0.5 ? '1px solid #6ab0f3' : '1px solid transparent'
                }}
              >
                {p.label}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Info panel */}
      <div style={{ 
        marginTop: '20px', 
        padding: '16px', 
        backgroundColor: '#232334', 
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div style={{ display: 'flex', gap: '24px', flexWrap: 'wrap', alignItems: 'center' }}>
          {viewMode === 'day' && (
            <>
              <div>
                <span style={{ color: '#a1a1a8' }}>Daylight: </span>
                <span style={{ color: '#8c7ae6' }}>{daylightHours.toFixed(1)}h</span>
              </div>
              {dayType === 'normal' && sunrisePoint && (
                <div>
                  <span style={{ color: '#a1a1a8' }}>Sunrise: </span>
                  <span style={{ color: '#e67e22' }}>
                    {Math.floor(sunrisePoint.hour)}:{((sunrisePoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
                  </span>
                </div>
              )}
              {dayType === 'normal' && sunsetPoint && (
                <div>
                  <span style={{ color: '#a1a1a8' }}>Sunset: </span>
                  <span style={{ color: '#e25f73' }}>
                    {Math.floor(sunsetPoint.hour)}:{((sunsetPoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}
                  </span>
                </div>
              )}
              {dayType !== 'normal' && (
                <div style={{ 
                  padding: '4px 12px', 
                  borderRadius: '4px',
                  backgroundColor: dayType === 'midnight-sun' ? 'rgba(244, 208, 63, 0.2)' : 'rgba(35, 35, 52, 0.8)',
                  border: `1px solid ${dayType === 'midnight-sun' ? '#f4d03f' : '#8c7ae6'}`
                }}>
                  {dayType === 'midnight-sun' ? '‚òÄÔ∏è Midnight Sun' : 'üåë Polar Night'}
                </div>
              )}
            </>
          )}
          {viewMode === 'year' && (
            <div style={{ fontSize: '12px', color: '#a1a1a8', fontStyle: 'italic' }}>
              Each oscillation = one day. Envelope shift = seasonal change.
            </div>
          )}
        </div>

        {/* Tilt info */}
        <div style={{ 
          marginTop: '12px', 
          padding: '12px', 
          backgroundColor: '#27272a', 
          borderRadius: '4px',
          fontSize: '12px',
          color: '#a1a1a8',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
          gap: '8px'
        }}>
          <div>
            <span style={{ color: '#e9e9ea' }}>Polar circles:</span> ¬±{polarCircle.toFixed(1)}¬∞
            {Math.abs(latitude) > polarCircle && <span style={{ color: '#e67e22' }}> ‚Üê inside!</span>}
          </div>
          <div>
            <span style={{ color: '#e9e9ea' }}>Tropics:</span> ¬±{axialTilt.toFixed(1)}¬∞
          </div>
          <div>
            <span style={{ color: '#e9e9ea' }}>Y-axis:</span> {yMin}¬∞ to {yMax}¬∞
          </div>
        </div>

        {axialTilt === 0 && (
          <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#1a1a1c', borderRadius: '4px', borderLeft: '3px solid #6ab0f3', fontSize: '12px', fontStyle: 'italic' }}>
            No axial tilt = eternal equinox. 12h days everywhere. No seasons.
          </div>
        )}
      </div>
    </div>
  );
};

export default SunPositionViz;

