import { useState, useMemo, useRef, useEffect, useCallback } from 'react';

const SunPositionViz = () => {
  const [latitude, setLatitude] = useState(45);
  const [minuteOfYear, setMinuteOfYear] = useState(172 * 24 * 60 + 12 * 60);
  const [axialTilt, setAxialTilt] = useState(23.45);
  const [viewMode, setViewMode] = useState('day');
  const [yAxisMode, setYAxisMode] = useState('dynamic');
  const [containerWidth, setContainerWidth] = useState(800);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playSpeed, setPlaySpeed] = useState(1);

  const containerRef = useRef(null);
  const animationRef = useRef(null);

  useEffect(() => {
    const updateWidth = () => {
      if (containerRef.current) {
        setContainerWidth(containerRef.current.offsetWidth);
      }
    };
    updateWidth();
    window.addEventListener('resize', updateWidth);
    return () => window.removeEventListener('resize', updateWidth);
  }, []);

  // Keyboard controls
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.target.tagName === 'INPUT') return;
      const step = e.shiftKey ? 60 : 15;
      if (e.key === 'ArrowRight') {
        setMinuteOfYear(prev => Math.min(prev + step, 365 * 24 * 60 - 1));
      } else if (e.key === 'ArrowLeft') {
        setMinuteOfYear(prev => Math.max(prev - step, 0));
      } else if (e.key === ' ') {
        e.preventDefault();
        setIsPlaying(p => !p);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Animation loop
  useEffect(() => {
    if (isPlaying) {
      const step = (viewMode === 'day' ? 15 : 60) * playSpeed;
      animationRef.current = setInterval(() => {
        setMinuteOfYear(prev => {
          const next = prev + step;
          return next >= 365 * 24 * 60 ? 0 : next;
        });
      }, 50);
    } else {
      clearInterval(animationRef.current);
    }
    return () => clearInterval(animationRef.current);
  }, [isPlaying, viewMode, playSpeed]);

  const width = containerWidth;
  const height = 300;
  const padding = { top: 35, right: 20, bottom: 55, left: 55 };
  const graphWidth = width - padding.left - padding.right;
  const graphHeight = height - padding.top - padding.bottom;

  const totalMinutesInYear = 365 * 24 * 60;

  const dayOfYear = Math.floor(minuteOfYear / (24 * 60)) + 1;
  const hourOfDay = (minuteOfYear % (24 * 60)) / 60;

  // Key astronomical values derived from tilt
  const tropicLat = axialTilt;
  const polarCircleLat = 90 - axialTilt;

  const getDeclination = useCallback((day) => {
    return axialTilt * Math.sin((2 * Math.PI / 365) * (day - 81));
  }, [axialTilt]);

  const declination = getDeclination(dayOfYear);

  const getDateTimeLabel = () => {
    const date = new Date(2024, 0, 1);
    date.setDate(dayOfYear);
    const dateStr = date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    const hours = Math.floor(hourOfDay);
    const mins = Math.round((hourOfDay % 1) * 60);
    return `${dateStr}, ${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
  };

  const getAltitude = useCallback((hour, decl) => {
    const decRad = (decl * Math.PI) / 180;
    const latRad = (latitude * Math.PI) / 180;
    const hourAngle = ((hour - 12) * 15 * Math.PI) / 180;
    const sinAltitude = 
      Math.sin(latRad) * Math.sin(decRad) + 
      Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle);
    return (Math.asin(Math.max(-1, Math.min(1, sinAltitude))) * 180) / Math.PI;
  }, [latitude]);

  const getAltitudeAtMinute = useCallback((minute) => {
    const day = Math.floor(minute / (24 * 60)) + 1;
    const hour = (minute % (24 * 60)) / 60;
    const decl = getDeclination(day);
    return getAltitude(hour, decl);
  }, [getDeclination, getAltitude]);

  const computeDayAltitudes = useCallback((decl) => {
    const points = [];
    for (let i = 0; i <= 240; i++) {
      const hour = i / 10;
      const altitude = getAltitude(hour, decl);
      points.push({ hour, altitude });
    }
    return points;
  }, [getAltitude]);

  const yearAltitudes = useMemo(() => {
    const points = [];
    const step = 120;
    for (let minute = 0; minute < totalMinutesInYear; minute += step) {
      const altitude = getAltitudeAtMinute(minute);
      points.push({ minute, altitude });
    }
    return points;
  }, [getAltitudeAtMinute]);

  const dayAltitudes = useMemo(() => computeDayAltitudes(declination), [computeDayAltitudes, declination]);
  const equinoxAltitudes = useMemo(() => computeDayAltitudes(0), [computeDayAltitudes]);

  const rawData = viewMode === 'day' ? dayAltitudes : yearAltitudes;

  const { yMin, yMax, yRange } = useMemo(() => {
    if (yAxisMode === 'fixed') return { yMin: -90, yMax: 90, yRange: 180 };
    if (yAxisMode === 'wide') return { yMin: -135, yMax: 135, yRange: 270 };

    const altitudes = rawData.map(p => p.altitude);
    const dataMin = Math.min(...altitudes);
    const dataMax = Math.max(...altitudes);
    const range = dataMax - dataMin;
    const paddingAmt = Math.max(range * 0.15, 8);

    let niceMin = Math.floor((dataMin - paddingAmt) / 10) * 10;
    let niceMax = Math.ceil((dataMax + paddingAmt) / 10) * 10;

    // Always include horizon if data crosses it
    if (dataMin < 0 && dataMax > 0) {
      niceMin = Math.min(niceMin, -15);
      niceMax = Math.max(niceMax, 15);
    }

    return { 
      yMin: Math.max(niceMin, -90), 
      yMax: Math.min(niceMax, 90), 
      yRange: Math.min(niceMax, 90) - Math.max(niceMin, -90) 
    };
  }, [rawData, yAxisMode]);

  const altToY = useCallback((alt) => padding.top + ((yMax - alt) / yRange) * graphHeight, [yMax, yRange, graphHeight]);

  const curveData = useMemo(() => {
    return rawData.map((p) => ({
      ...p,
      x: padding.left + ((viewMode === 'day' ? p.hour / 24 : p.minute / totalMinutesInYear)) * graphWidth,
      y: altToY(p.altitude)
    }));
  }, [rawData, graphWidth, altToY, viewMode]);

  const equinoxCurve = useMemo(() => {
    if (viewMode !== 'day') return null;
    return equinoxAltitudes.map((p) => ({
      ...p,
      x: padding.left + (p.hour / 24) * graphWidth,
      y: altToY(p.altitude)
    }));
  }, [equinoxAltitudes, graphWidth, altToY, viewMode]);

  const pathD = curveData.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ');
  const equinoxPathD = equinoxCurve ? equinoxCurve.map((p, i) => `${i === 0 ? 'M' : 'L'} ${p.x} ${p.y}`).join(' ') : '';

  const horizonY = altToY(0);
  const horizonVisible = yMin <= 0 && yMax >= 0;
  const zenithY = altToY(90);
  const nadirY = altToY(-90);
  const showPhysicalLimits = yAxisMode === 'wide';

  const currentAltitude = getAltitudeAtMinute(minuteOfYear);
  const currentX = viewMode === 'day' 
    ? padding.left + (hourOfDay / 24) * graphWidth
    : padding.left + (minuteOfYear / totalMinutesInYear) * graphWidth;
  const currentY = altToY(currentAltitude);

  const dayType = viewMode === 'day' ? (() => {
    const minAlt = Math.min(...rawData.map(p => p.altitude));
    const maxAlt = Math.max(...rawData.map(p => p.altitude));
    return minAlt > 0 ? 'midnight-sun' : maxAlt < 0 ? 'polar-night' : 'normal';
  })() : 'normal';

  const sunrisePoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude < 0 && p.altitude >= 0) : null;
  const sunsetPoint = viewMode === 'day' ? curveData.find((p, i) => i > 0 && curveData[i-1].altitude >= 0 && p.altitude < 0) : null;

  const daylightHours = viewMode === 'day' ? (
    dayType === 'midnight-sun' ? 24 : dayType === 'polar-night' ? 0 
    : (curveData.filter(p => p.altitude >= 0).length / curveData.length) * 24
  ) : null;

  // Key dates (day of year)
  const keyDates = {
    vernalEquinox: 80,
    summerSolstice: 172,
    autumnalEquinox: 266,
    winterSolstice: 355
  };

  const tiltPresets = [
    { value: 0, label: '0¬∞' },
    { value: 23.45, label: 'Earth' },
    { value: 25.19, label: 'Mars' },
    { value: 82.23, label: 'Uranus' },
  ];

  // Dynamic latitude markers based on tilt
  const latMarkers = useMemo(() => {
    const markers = [
      { value: 0, label: 'Equator', color: '#a1a1a8' },
    ];

    if (axialTilt > 0) {
      markers.push(
        { value: tropicLat, label: `Cancer ${tropicLat.toFixed(1)}¬∞`, color: '#f4d03f' },
        { value: -tropicLat, label: `Capricorn`, color: '#f4d03f' },
      );

      if (polarCircleLat < 90) {
        markers.push(
          { value: polarCircleLat, label: `Arctic ${polarCircleLat.toFixed(1)}¬∞`, color: '#6ab0f3' },
          { value: -polarCircleLat, label: `Antarctic`, color: '#6ab0f3' },
        );
      }
    }

    markers.push(
      { value: 90, label: '90¬∞N', color: '#a1a1a8' },
      { value: -90, label: '90¬∞S', color: '#a1a1a8' },
    );

    return markers.sort((a, b) => a.value - b.value);
  }, [axialTilt, tropicLat, polarCircleLat]);

  const monthData = [
    { day: 1, label: 'Jan' }, { day: 32, label: 'Feb' }, { day: 60, label: 'Mar' },
    { day: 91, label: 'Apr' }, { day: 121, label: 'May' }, { day: 152, label: 'Jun' },
    { day: 182, label: 'Jul' }, { day: 213, label: 'Aug' }, { day: 244, label: 'Sep' },
    { day: 274, label: 'Oct' }, { day: 305, label: 'Nov' }, { day: 335, label: 'Dec' },
  ];

  const timeMarkers = [
    { minute: keyDates.winterSolstice * 24 * 60, label: 'Winter Sol', short: 'W' },
    { minute: keyDates.vernalEquinox * 24 * 60, label: 'Vernal Eq', short: 'V' },
    { minute: keyDates.summerSolstice * 24 * 60, label: 'Summer Sol', short: 'S' },
    { minute: keyDates.autumnalEquinox * 24 * 60, label: 'Autumn Eq', short: 'A' },
  ];

  const yGridLines = useMemo(() => {
    const lines = [];
    const step = yRange <= 60 ? 10 : yRange <= 120 ? 20 : 30;
    for (let alt = Math.ceil(yMin / step) * step; alt <= yMax; alt += step) {
      lines.push(alt);
    }
    return lines;
  }, [yMin, yMax, yRange]);

  const handleMonthClick = (day) => {
    const minute = (day - 1) * 24 * 60 + 12 * 60;
    setMinuteOfYear(minute);
  };

  const handleHourClick = (hour) => {
    const dayStart = Math.floor(minuteOfYear / (24 * 60)) * 24 * 60;
    setMinuteOfYear(dayStart + hour * 60);
  };

  const handleTimeMarkerClick = (minute) => {
    setMinuteOfYear(minute + 12 * 60); // noon on that day
  };

  // Latitude zone description
  const getLatitudeZone = () => {
    const absLat = Math.abs(latitude);
    if (absLat <= tropicLat) return { zone: 'Tropical', color: '#f4d03f' };
    if (absLat < polarCircleLat) return { zone: 'Temperate', color: '#a1a1a8' };
    return { zone: 'Polar', color: '#6ab0f3' };
  };

  const latZone = getLatitudeZone();

  return (
    <div 
      ref={containerRef}
      tabIndex={0}
      style={{ 
        backgroundColor: '#1a1a1c', 
        padding: '20px', 
        borderRadius: '8px',
        fontFamily: 'system-ui, -apple-system, sans-serif',
        color: '#e9e9ea',
        width: '100%',
        boxSizing: 'border-box',
        outline: 'none'
      }}
    >
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px', flexWrap: 'wrap', gap: '10px' }}>
        <h2 style={{ fontSize: '18px', margin: 0 }}>
          Solar Altitude ‚Äî {viewMode === 'day' ? 'Daily Cycle' : 'Annual Cycle'}
        </h2>

        <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
          <div style={{ display: 'flex', gap: '2px', backgroundColor: '#27272a', padding: '3px', borderRadius: '6px' }}>
            {['dynamic', 'fixed', 'wide'].map(mode => (
              <button
                key={mode}
                onClick={() => setYAxisMode(mode)}
                style={{
                  padding: '4px 8px',
                  borderRadius: '4px',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '11px',
                  backgroundColor: yAxisMode === mode ? '#6ab0f3' : 'transparent',
                  color: yAxisMode === mode ? '#1a1a1c' : '#a1a1a8',
                }}
              >
                {mode === 'dynamic' ? 'Auto' : mode === 'fixed' ? '¬±90¬∞' : '¬±135¬∞'}
              </button>
            ))}
          </div>

          <div style={{ display: 'flex', gap: '2px', backgroundColor: '#27272a', padding: '3px', borderRadius: '6px' }}>
            {['day', 'year'].map(mode => (
              <button
                key={mode}
                onClick={() => setViewMode(mode)}
                style={{
                  padding: '4px 10px',
                  borderRadius: '4px',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '11px',
                  backgroundColor: viewMode === mode ? '#8c7ae6' : 'transparent',
                  color: viewMode === mode ? '#1a1a1c' : '#a1a1a8',
                }}
              >
                {mode === 'day' ? '24h' : '365d'}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Graph */}
      <svg width={width} height={height} style={{ display: 'block' }}>
        <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} fill="#27272a" rx="4" />

        <defs>
          <clipPath id="graphClip">
            <rect x={padding.left} y={padding.top} width={graphWidth} height={graphHeight} />
          </clipPath>
          <linearGradient id="sunGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" stopColor="#f4d03f" />
            <stop offset="100%" stopColor="#e67e22" />
          </linearGradient>
        </defs>

        {/* Physical limits zone (wide mode) */}
        {showPhysicalLimits && (
          <>
            <rect x={padding.left} y={padding.top} width={graphWidth} height={Math.max(0, zenithY - padding.top)}
              fill="rgba(226, 95, 115, 0.12)" clipPath="url(#graphClip)" />
            <rect x={padding.left} y={nadirY} width={graphWidth} height={Math.max(0, padding.top + graphHeight - nadirY)}
              fill="rgba(226, 95, 115, 0.12)" clipPath="url(#graphClip)" />
            <line x1={padding.left} y1={zenithY} x2={padding.left + graphWidth} y2={zenithY}
              stroke="#e25f73" strokeWidth="1" strokeDasharray="4,4" opacity="0.6" />
            <text x={padding.left + graphWidth - 5} y={zenithY + 12} fill="#e25f73" fontSize="9" textAnchor="end">+90¬∞ zenith</text>
            <line x1={padding.left} y1={nadirY} x2={padding.left + graphWidth} y2={nadirY}
              stroke="#e25f73" strokeWidth="1" strokeDasharray="4,4" opacity="0.6" />
            <text x={padding.left + graphWidth - 5} y={nadirY - 5} fill="#e25f73" fontSize="9" textAnchor="end">-90¬∞ nadir</text>
          </>
        )}

        {/* Night region */}
        {horizonVisible && (
          <rect x={padding.left} y={horizonY} width={graphWidth} 
            height={Math.min(showPhysicalLimits ? nadirY : padding.top + graphHeight, padding.top + graphHeight) - horizonY}
            fill="rgba(35, 35, 52, 0.5)" clipPath="url(#graphClip)" />
        )}

        {/* Horizon line */}
        {horizonVisible && (
          <>
            <line x1={padding.left} y1={horizonY} x2={padding.left + graphWidth} y2={horizonY}
              stroke="#e67e22" strokeWidth="2" strokeDasharray="8,4" />
            <text x={padding.left + graphWidth - 5} y={horizonY - 5} fill="#e67e22" fontSize="10" textAnchor="end">
              horizon (0¬∞)
            </text>
          </>
        )}

        {/* Grid - altitude */}
        {yGridLines.map(alt => {
          const y = altToY(alt);
          if (alt === 0 && horizonVisible) return null;
          if (showPhysicalLimits && (alt === 90 || alt === -90)) return null;
          return (
            <g key={alt}>
              <line x1={padding.left} y1={y} x2={padding.left + graphWidth} y2={y} stroke="#393941" strokeWidth="1" />
              <text x={padding.left - 8} y={y + 4} fill="#a1a1a8" fontSize="10" textAnchor="end">{alt}¬∞</text>
            </g>
          );
        })}

        {/* Grid - time (day view) */}
        {viewMode === 'day' && [0, 6, 12, 18, 24].map(hour => {
          const x = padding.left + (hour / 24) * graphWidth;
          const labels = { 0: 'Midnight', 6: '6:00', 12: 'Noon', 18: '18:00', 24: 'Midnight' };
          const isActive = Math.abs(hourOfDay - hour) < 1 || (hour === 24 && hourOfDay < 1);
          return (
            <g key={hour}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text 
                x={x} y={padding.top + graphHeight + 14} 
                fill={isActive ? '#f4d03f' : '#a1a1a8'} 
                fontSize="10" 
                textAnchor="middle"
                style={{ cursor: 'pointer' }}
                onClick={() => handleHourClick(hour === 24 ? 0 : hour)}
              >
                {labels[hour]}
              </text>
            </g>
          );
        })}

        {/* Grid - months (year view) */}
        {viewMode === 'year' && monthData.map(m => {
          const x = padding.left + ((m.day - 1) * 24 * 60 / totalMinutesInYear) * graphWidth;
          const isActive = dayOfYear >= m.day && dayOfYear < m.day + 30;
          return (
            <g key={m.day}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} stroke="#393941" strokeWidth="1" />
              <text 
                x={x + 12} y={padding.top + graphHeight + 14} 
                fill={isActive ? '#f4d03f' : '#a1a1a8'} 
                fontSize="10" 
                textAnchor="middle"
                style={{ cursor: 'pointer' }}
                onClick={() => handleMonthClick(m.day)}
              >
                {m.label}
              </text>
            </g>
          );
        })}

        {/* Solstice/Equinox markers (year view) */}
        {viewMode === 'year' && timeMarkers.map(tm => {
          const x = padding.left + (tm.minute / totalMinutesInYear) * graphWidth;
          const isNear = Math.abs(minuteOfYear - tm.minute) < 7 * 24 * 60;
          return (
            <g key={tm.label}>
              <line x1={x} y1={padding.top} x2={x} y2={padding.top + graphHeight} 
                stroke={isNear ? '#8c7ae6' : '#555'} strokeWidth="1" strokeDasharray="2,4" />
              <text 
                x={x} y={padding.top + graphHeight + 26} 
                fill={isNear ? '#8c7ae6' : '#666'} 
                fontSize="8" 
                textAnchor="middle"
                style={{ cursor: 'pointer' }}
                onClick={() => handleTimeMarkerClick(tm.minute)}
              >
                {tm.short}
              </text>
            </g>
          );
        })}

        {/* Equinox reference curve (day view) */}
        {viewMode === 'day' && axialTilt > 0 && Math.abs(declination) > 2 && equinoxCurve && (
          <>
            <path d={equinoxPathD} fill="none" stroke="#a1a1a8" strokeWidth="1" strokeDasharray="4,4" opacity="0.35" clipPath="url(#graphClip)" />
            <text x={padding.left + graphWidth - 5} y={equinoxCurve[120].y - 5} fill="#a1a1a8" fontSize="8" textAnchor="end" opacity="0.6">equinox</text>
          </>
        )}

        {/* Sun path curve */}
        <path d={pathD} fill="none" stroke="url(#sunGradient)" strokeWidth={viewMode === 'day' ? 3 : 1.5} clipPath="url(#graphClip)" />

        {/* Sunrise marker */}
        {viewMode === 'day' && sunrisePoint && (
          <g>
            <circle cx={sunrisePoint.x} cy={sunrisePoint.y} r="5" fill="#e67e22" />
            <text x={sunrisePoint.x} y={sunrisePoint.y + 14} fill="#e67e22" fontSize="8" textAnchor="middle">‚Üërise</text>
          </g>
        )}

        {/* Sunset marker */}
        {viewMode === 'day' && sunsetPoint && (
          <g>
            <circle cx={sunsetPoint.x} cy={sunsetPoint.y} r="5" fill="#e25f73" />
            <text x={sunsetPoint.x} y={sunsetPoint.y + 14} fill="#e25f73" fontSize="8" textAnchor="middle">set‚Üì</text>
          </g>
        )}

        {/* Current position marker */}
        <g>
          <circle cx={currentX} cy={currentY} r="7" fill="#8c7ae6" stroke="#1a1a1c" strokeWidth="2" />
          {/* Vertical reference line */}
          <line x1={currentX} y1={padding.top} x2={currentX} y2={padding.top + graphHeight} 
            stroke="#8c7ae6" strokeWidth="1" strokeDasharray="2,4" opacity="0.3" />
        </g>

        {/* Y-axis label */}
        <text x={14} y={padding.top + graphHeight / 2} fill="#a1a1a8" fontSize="10" textAnchor="middle" 
          transform={`rotate(-90, 14, ${padding.top + graphHeight / 2})`}>
          Solar Altitude (¬∞)
        </text>

        {/* X-axis label */}
        <text x={padding.left + graphWidth / 2} y={height - 5} fill="#a1a1a8" fontSize="10" textAnchor="middle">
          {viewMode === 'day' ? 'Local Solar Time' : 'Day of Year (V=Vernal Eq, S=Summer Sol, A=Autumn Eq, W=Winter Sol)'}
        </text>
      </svg>

      {/* Time slider - directly under graph */}
      <div style={{ padding: '8px 0 4px 0', display: 'flex', alignItems: 'center', gap: '8px' }}>
        <button
          onClick={() => setIsPlaying(!isPlaying)}
          style={{
            width: '32px',
            height: '28px',
            borderRadius: '6px',
            border: '1px solid #393941',
            cursor: 'pointer',
            fontSize: '12px',
            backgroundColor: isPlaying ? '#8c7ae6' : 'transparent',
            color: isPlaying ? '#1a1a1c' : '#a1a1a8',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          {isPlaying ? '‚è∏' : '‚ñ∂'}
        </button>

        <select
          value={playSpeed}
          onChange={(e) => setPlaySpeed(Number(e.target.value))}
          style={{
            backgroundColor: '#27272a',
            color: '#a1a1a8',
            border: '1px solid #393941',
            borderRadius: '4px',
            padding: '4px',
            fontSize: '10px',
            cursor: 'pointer'
          }}
        >
          <option value={0.5}>0.5√ó</option>
          <option value={1}>1√ó</option>
          <option value={2}>2√ó</option>
          <option value={4}>4√ó</option>
        </select>

        <div style={{ flex: 1 }}>
          <input 
            type="range" 
            min={0} 
            max={totalMinutesInYear - 1} 
            step={15}
            value={minuteOfYear}
            onChange={(e) => setMinuteOfYear(parseInt(e.target.value, 10))}
            style={{ width: '100%', accentColor: '#f4d03f' }}
          />
        </div>

        <div style={{ minWidth: '110px', fontSize: '12px', textAlign: 'right' }}>
          <span style={{ color: '#f4d03f' }}>{getDateTimeLabel()}</span>
        </div>
      </div>

      {/* Time marker shortcuts */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '8px', marginBottom: '10px', flexWrap: 'wrap' }}>
        {timeMarkers.map(tm => {
          const isActive = Math.abs(minuteOfYear - tm.minute) < 3 * 24 * 60;
          return (
            <button
              key={tm.label}
              onClick={() => handleTimeMarkerClick(tm.minute)}
              style={{
                padding: '3px 8px',
                borderRadius: '4px',
                border: 'none',
                cursor: 'pointer',
                fontSize: '10px',
                backgroundColor: isActive ? 'rgba(140, 122, 230, 0.25)' : '#27272a',
                color: isActive ? '#8c7ae6' : '#a1a1a8',
              }}
            >
              {tm.label}
            </button>
          );
        })}
      </div>

      {/* Stats row */}
      <div style={{ 
        display: 'flex', 
        gap: '12px', 
        flexWrap: 'wrap', 
        padding: '10px 12px',
        backgroundColor: '#232334',
        borderRadius: '6px',
        fontSize: '11px',
        marginBottom: '12px',
        alignItems: 'center'
      }}>
        <div>
          <span style={{ color: '#a1a1a8' }}>Altitude: </span>
          <span style={{ color: currentAltitude >= 0 ? '#f4d03f' : '#6ab0f3', fontWeight: 'bold' }}>{currentAltitude.toFixed(1)}¬∞</span>
        </div>
        <div>
          <span style={{ color: '#a1a1a8' }}>Declination: </span>
          <span style={{ color: declination >= 0 ? '#f4d03f' : '#6ab0f3' }}>{declination >= 0 ? '+' : ''}{declination.toFixed(1)}¬∞</span>
        </div>
        {viewMode === 'day' && (
          <>
            <div>
              <span style={{ color: '#a1a1a8' }}>Daylight: </span>
              <span style={{ color: '#8c7ae6' }}>{daylightHours.toFixed(1)}h</span>
            </div>
            {dayType === 'normal' && sunrisePoint && (
              <div>
                <span style={{ color: '#e67e22' }}>‚Üë</span>
                <span style={{ color: '#a1a1a8' }}> {Math.floor(sunrisePoint.hour)}:{((sunrisePoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}</span>
              </div>
            )}
            {dayType === 'normal' && sunsetPoint && (
              <div>
                <span style={{ color: '#e25f73' }}>‚Üì</span>
                <span style={{ color: '#a1a1a8' }}> {Math.floor(sunsetPoint.hour)}:{((sunsetPoint.hour % 1) * 60).toFixed(0).padStart(2, '0')}</span>
              </div>
            )}
            {dayType !== 'normal' && (
              <div style={{ 
                padding: '2px 8px', 
                borderRadius: '4px',
                backgroundColor: dayType === 'midnight-sun' ? 'rgba(244, 208, 63, 0.2)' : 'rgba(140, 122, 230, 0.2)',
                border: `1px solid ${dayType === 'midnight-sun' ? '#f4d03f' : '#8c7ae6'}`,
                fontSize: '10px'
              }}>
                {dayType === 'midnight-sun' ? '‚òÄÔ∏è Midnight Sun' : 'üåë Polar Night'}
              </div>
            )}
          </>
        )}
        <div style={{ marginLeft: 'auto', fontSize: '9px', color: '#666' }}>
          ‚Üê ‚Üí keys: adjust time | Space: play/pause
        </div>
      </div>

      {/* Controls */}
      <div style={{ display: 'flex', gap: '16px', flexWrap: 'wrap' }}>
        {/* Latitude with dynamic markers */}
        <div style={{ flex: 1, minWidth: '300px' }}>
          <label style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '6px', fontSize: '12px' }}>
            <span>
              Latitude: <span style={{ color: '#8c7ae6' }}>{latitude}¬∞</span>
              {latitude !== 0 && <span style={{ color: '#a1a1a8' }}> {latitude > 0 ? 'N' : 'S'}</span>}
            </span>
            <span style={{ color: latZone.color, fontSize: '10px' }}>
              {latZone.zone} Zone
            </span>
          </label>

          {/* Custom slider with markers */}
          <div style={{ position: 'relative', height: '24px', marginBottom: '4px' }}>
            <input 
              type="range" min={-90} max={90} value={latitude}
              onChange={(e) => setLatitude(parseInt(e.target.value, 10))}
              style={{ width: '100%', accentColor: '#8c7ae6', position: 'absolute', top: '0' }}
            />

            {/* Dynamic marker lines */}
            {axialTilt > 0 && (
              <div style={{ position: 'absolute', top: '14px', left: '0', right: '0', height: '10px', pointerEvents: 'none' }}>
                {/* Tropics */}
                <div style={{ position: 'absolute', left: `${((tropicLat + 90) / 180) * 100}%`, width: '1px', height: '6px', backgroundColor: '#f4d03f', opacity: 0.7 }} />
                <div style={{ position: 'absolute', left: `${((-tropicLat + 90) / 180) * 100}%`, width: '1px', height: '6px', backgroundColor: '#f4d03f', opacity: 0.7 }} />
                {/* Polar circles */}
                {polarCircleLat < 90 && (
                  <>
                    <div style={{ position: 'absolute', left: `${((polarCircleLat + 90) / 180) * 100}%`, width: '1px', height: '6px', backgroundColor: '#6ab0f3', opacity: 0.7 }} />
                    <div style={{ position: 'absolute', left: `${((-polarCircleLat + 90) / 180) * 100}%`, width: '1px', height: '6px', backgroundColor: '#6ab0f3', opacity: 0.7 }} />
                  </>
                )}
              </div>
            )}
          </div>

          {/* Clickable labels */}
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '9px', flexWrap: 'wrap', gap: '2px' }}>
            {latMarkers.filter((m, i, arr) => {
              // Filter out overlapping markers
              return !arr.some((other, j) => j < i && Math.abs(other.value - m.value) < 5);
            }).map(m => (
              <span 
                key={m.value}
                onClick={() => setLatitude(Math.round(m.value))}
                style={{ 
                  cursor: 'pointer', 
                  padding: '2px 3px',
                  borderRadius: '3px',
                  backgroundColor: Math.abs(latitude - m.value) < 3 ? 'rgba(140, 122, 230, 0.2)' : 'transparent',
                  color: Math.abs(latitude - m.value) < 3 ? '#8c7ae6' : m.color,
                  whiteSpace: 'nowrap'
                }}
              >
                {m.label}
              </span>
            ))}
          </div>
        </div>

        {/* Axial Tilt */}
        <div style={{ flex: 1, minWidth: '220px' }}>
          <label style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '6px', fontSize: '12px' }}>
            <span>Axial Tilt: <span style={{ color: '#6ab0f3' }}>{axialTilt.toFixed(1)}¬∞</span></span>
            <span style={{ fontSize: '9px', color: '#666' }}>
              Tropics: ¬±{tropicLat.toFixed(1)}¬∞ | Polar: ¬±{polarCircleLat.toFixed(1)}¬∞
            </span>
          </label>
          <input 
            type="range" min={0} max={90} step={0.1} value={axialTilt}
            onChange={(e) => setAxialTilt(parseFloat(e.target.value))}
            style={{ width: '100%', accentColor: '#6ab0f3' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', marginTop: '4px' }}>
            {tiltPresets.map(p => (
              <span 
                key={p.value}
                onClick={() => setAxialTilt(p.value)}
                style={{ 
                  cursor: 'pointer', 
                  padding: '2px 6px',
                  borderRadius: '3px',
                  backgroundColor: Math.abs(axialTilt - p.value) < 0.5 ? 'rgba(106, 176, 243, 0.2)' : 'transparent',
                  color: Math.abs(axialTilt - p.value) < 0.5 ? '#6ab0f3' : '#a1a1a8',
                }}
              >
                {p.label}
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* Educational note */}
      {axialTilt === 0 && (
        <div style={{ marginTop: '12px', padding: '8px 12px', backgroundColor: '#27272a', borderRadius: '6px', borderLeft: '3px solid #6ab0f3', fontSize: '11px', color: '#a1a1a8' }}>
          <strong style={{ color: '#6ab0f3' }}>No axial tilt:</strong> Eternal equinox ‚Äî every location has 12-hour days year-round. No seasons, no tropics, no polar circles.
        </div>
      )}
    </div>
  );
};

export default SunPositionViz;
